class TreeNode {
  constructor(value) {
    this.value = value;
    this.left = null;
    this.right = null;
  }
}

// Pre-order Traversal (Root, Left, Right)
function preorderTraversalRecursive(root) {
  const result = [];
  function traverse(node) {
    if (node === null) return;
    result.push(node.value);
    traverse(node.left);
    traverse(node.right);
  }
  traverse(root);
  return result;
}

function preorderTraversalIterative(root) {
  const result = [];
  const stack = [];
  if (!root) return result;
  stack.push(root);
  while (stack.length > 0) {
    const current = stack.pop();
    result.push(current.value);
    if (current.right) stack.push(current.right);
    if (current.left) stack.push(current.left);
  }
  return result;
}

// In-order Traversal (Left, Root, Right)
function inorderTraversalRecursive(root) {
  const result = [];
  function traverse(node) {
    if (node === null) return;
    traverse(node.left);
    result.push(node.value);
    traverse(node.right);
  }
  traverse(root);
  return result;
}

function inorderTraversalIterative(root) {
  const result = [];
  const stack = [];
  let current = root;

  while (current !== null || stack.length > 0) {
    while (current !== null) {
      stack.push(current);
      current = current.left;
    }
    current = stack.pop();
    result.push(current.value);
    current = current.right;
  }
  return result;
}

// Post-order Traversal (Left, Right, Root)
function postorderTraversalRecursive(root) {
  const result = [];
  function traverse(node) {
    if (node === null) return;
    traverse(node.left);
    traverse(node.right);
    result.push(node.value);
  }
  traverse(root);
  return result;
}

function postorderTraversalIterative(root) {
  const result = [];
  const stack1 = [];
  const stack2 = [];
  if (!root) return result;

  stack1.push(root);
  while (stack1.length > 0) {
    const current = stack1.pop();
    stack2.push(current.value);
    if (current.left) stack1.push(current.left);
    if (current.right) stack1.push(current.right);
  }
  // Reverse stack2 to get post-order sequence
  while (stack2.length > 0) {
    result.push(stack2.pop());
  }
  return result;
}

