function flattenObject(obj, separator = '.', prefix = '') {
  const result = {};

  for (const key in obj) {
    if (!obj.hasOwnProperty(key)) continue; // Ensure own properties

    const newKey = prefix ? `${prefix}${separator}${key}` : key;
    const value = obj[key];

    if (typeof value === 'object' && value !== null) {
      if (Array.isArray(value)) {
        // Handle arrays
        value.forEach((item, index) => {
          const arrayItemKey = `${newKey}[${index}]`;
          if (typeof item === 'object' && item !== null) {
            Object.assign(result, flattenObject(item, separator, arrayItemKey));
          } else {
            result[arrayItemKey] = item;
          }
        });
      } else {
        // Handle plain objects
        Object.assign(result, flattenObject(value, separator, newKey));
      }
    } else {
      // Handle primitive values, null, undefined
      result[newKey] = value;
    }
  }
  return result;
}

