function unflattenObject(flattenedObj, separator = '.') {
  const result = {};

  for (const key in flattenedObj) {
    if (!flattenedObj.hasOwnProperty(key)) continue;

    const value = flattenedObj[key];
    const parts = key.split(separator);
    let current = result;

    for (let i = 0; i < parts.length; i++) {
      let part = parts[i];
      let isArrayPart = false;
      let arrayKey = '';
      let arrayIndex = -1;

      // Check for array notation e.g., 'key[index]'
      const arrayMatch = part.match(/(.*)\[(\d+)\]$/);
      if (arrayMatch) {
        isArrayPart = true;
        arrayKey = arrayMatch[1];
        arrayIndex = parseInt(arrayMatch[2], 10);
        part = arrayKey; // Use the base key for property access
      }

      // If it's the last part of the path, assign the value
      if (i === parts.length - 1) {
        if (isArrayPart) {
          if (!current[part]) {
            current[part] = [];
          } else if (!Array.isArray(current[part])) {
            // Path conflict: e.g., 'a' was an object, now 'a[0]' expects array
            console.warn(`Type conflict at path segment '${part}': Expected array but found object. Overwriting.`);
            current[part] = [];
          }
          current[part][arrayIndex] = value;
        } else {
          current[part] = value;
        }
      } else { // Not the last part, ensure intermediate container exists
        if (isArrayPart) {
          if (!current[part]) {
            current[part] = [];
          } else if (!Array.isArray(current[part])) {
             console.warn(`Type conflict at path segment '${part}': Expected array but found object. Overwriting.`);
             current[part] = [];
          }

          // Ensure nested object/array exists at the array index
          if (!current[part][arrayIndex] || typeof current[part][arrayIndex] !== 'object' || Array.isArray(current[part][arrayIndex])) {
            current[part][arrayIndex] = {}; // Assume it should be an object for further nesting
          }
          current = current[part][arrayIndex];
        } else { // Regular object part
          if (!current[part] || typeof current[part] !== 'object' || Array.isArray(current[part])) {
            current[part] = {}; // Assume it should be an object for further nesting
          }
          current = current[part];
        }
      }
    }
  }
  return result;
}

