class Vehicle {
  constructor(licensePlate, type) {
    this.licensePlate = licensePlate;
    this.type = type; // e.g., 'car', 'motorcycle', 'bus'
    this.entryTime = null;
  }
}

class ParkingLot {
  constructor(capacityConfig) {
    this.capacityConfig = capacityConfig;
    this.totalCapacity = Object.values(capacityConfig).reduce((sum, count) => sum + count, 0);

    this.slotsMap = new Map(); // slotId -> { vehicle: Vehicle | null, type: 'CAR' | 'MOTORCYCLE' | 'BUS' }
    this.availableSlotsByType = {
        'CAR': new Set(),
        'MOTORCYCLE': new Set(),
        'BUS': new Set()
    };

    let slotCounter = 0;
    for (const type in capacityConfig) {
        for (let i = 0; i < capacityConfig[type]; i++) {
            const slotId = `${type.toUpperCase()}_${i + 1}`;
            this.slotsMap.set(slotId, { vehicle: null, type: type.toUpperCase(), id: slotId });
            this.availableSlotsByType[type.toUpperCase()].add(slotId);
        }
    }

    this.occupiedVehiclesMap = new Map(); // licensePlate -> { vehicle: Vehicle, slotId: string }
  }

  _getParkingPreference(vehicleType) {
    // Define preference order for different vehicle types
    const preferences = {
      'MOTORCYCLE': ['MOTORCYCLE', 'CAR', 'BUS'],
      'CAR': ['CAR', 'BUS'],
      'BUS': ['BUS']
    };
    return preferences[vehicleType];
  }

  _calculateFee(vehicle) {
    const rates = {
      'MOTORCYCLE': 2,
      'CAR': 5,
      'BUS': 10
    };
    if (!vehicle.entryTime) return 0; 
    const durationHours = Math.ceil((Date.now() - vehicle.entryTime) / (1000 * 60 * 60));
    return durationHours * (rates[vehicle.type] || 0);
  }

  parkVehicle(licensePlate, type) {
    const vehicleType = type.toUpperCase();
    if (this.occupiedVehiclesMap.has(licensePlate)) {
      console.log(`Vehicle ${licensePlate} is already parked.`);
      return null;
    }

    const preferredSlotTypes = this._getParkingPreference(vehicleType);
    let foundSlotId = null;

    for (const slotType of preferredSlotTypes) {
      if (this.availableSlotsByType[slotType] && this.availableSlotsByType[slotType].size > 0) {
        // Get the first available slot of this type
        foundSlotId = this.availableSlotsByType[slotType].values().next().value;
        this.availableSlotsByType[slotType].delete(foundSlotId);
        break; 
      }
    }

    if (!foundSlotId) {
      console.log(`Sorry, no suitable parking slot available for ${vehicleType} (License: ${licensePlate}).`);
      return null;
    }

    const vehicle = new Vehicle(licensePlate, vehicleType);
    vehicle.entryTime = Date.now();
    this.slotsMap.get(foundSlotId).vehicle = vehicle;
    this.occupiedVehiclesMap.set(licensePlate, { vehicle, slotId: foundSlotId });

    console.log(`Vehicle ${licensePlate} (${vehicleType}) parked at slot ${foundSlotId}.`);
    return foundSlotId;
  }

  unparkVehicle(licensePlate) {
    if (!this.occupiedVehiclesMap.has(licensePlate)) {
      console.log(`Vehicle ${licensePlate} not found in parking lot.`);
      return null;
    }

    const { vehicle, slotId } = this.occupiedVehiclesMap.get(licensePlate);

    // Free up the slot
    this.slotsMap.get(slotId).vehicle = null;
    this.availableSlotsByType[this.slotsMap.get(slotId).type].add(slotId);
    this.occupiedVehiclesMap.delete(licensePlate);

    const fee = this._calculateFee(vehicle);

    console.log(`Vehicle ${licensePlate} unparked from slot ${slotId}. Duration: ${((Date.now() - vehicle.entryTime) / (1000 * 60 * 60)).toFixed(2)} hours. Fee: $${fee}.`);
    return { vehicle, fee };
  }

  getAvailableSlots(vehicleType) {
    const type = vehicleType.toUpperCase();
    const relevantSlotTypes = this._getParkingPreference(type);
    let count = 0;
    for (const slotType of relevantSlotTypes) {
      if (this.availableSlotsByType[slotType]) {
        count += this.availableSlotsByType[slotType].size;
      }
    }
    return count;
  }

  getOccupiedSlots() {
    const occupied = [];
    for (const [slotId, slotInfo] of this.slotsMap.entries()) {
      if (slotInfo.vehicle) {
        occupied.push({
          slotId: slotId,
          vehicle: slotInfo.vehicle,
          slotType: slotInfo.type
        });
      }
    }
    return occupied;
  }
}

