class Vehicle {
  registrationNumber: string;
  color: string;

  constructor(registrationNumber: string, color: string) {
    this.registrationNumber = registrationNumber;
    this.color = color;
  }
}

interface ParkResult {
  success: boolean;
  message: string;
  slotNumber?: number;
}

interface RemoveResult {
  success: boolean;
  message: string;
  vehicle?: Vehicle;
}

interface VehicleInSlot {
  vehicle: Vehicle;
  slotNumber: number;
}

class ParkingLot {
  private capacity: number;
  private slots: (Vehicle | null)[];
  private availableSlots: Set<number>;

  constructor(capacity: number) {
    this.capacity = capacity;
    this.slots = new Array<Vehicle | null>(capacity).fill(null);
    this.availableSlots = new Set<number>();
    for (let i = 0; i < capacity; i++) {
      this.availableSlots.add(i);
    }
  }

  parkVehicle(vehicle: Vehicle): ParkResult {
    if (this.availableSlots.size === 0) {
      return { success: false, message: "No available parking slots" };
    }

    const slotNumber: number = this.availableSlots.values().next().value;
    this.availableSlots.delete(slotNumber);

    this.slots[slotNumber] = vehicle;

    return { 
      success: true, 
      message: `Vehicle parked successfully`, 
      slotNumber 
    };
  }

  removeVehicle(slotNumber: number): RemoveResult {
    if (slotNumber < 0 || slotNumber >= this.capacity) {
      return { success: false, message: "Invalid slot number" };
    }

    if (this.slots[slotNumber] === null) {
      return { success: false, message: "Slot is already empty" };
    }

    const vehicle: Vehicle = this.slots[slotNumber] as Vehicle; // Type assertion as it's guaranteed not null here
    this.slots[slotNumber] = null;
    this.availableSlots.add(slotNumber);

    return { 
      success: true, 
      message: "Vehicle removed successfully", 
      vehicle 
    };
  }

  getVehiclesByColor(color: string): VehicleInSlot[] {
    return this.slots
      .map((vehicle, index) => ({ vehicle, slotNumber: index }))
      .filter((item): item is VehicleInSlot => item.vehicle !== null && item.vehicle.color === color)
      .map(item => ({ vehicle: item.vehicle, slotNumber: item.slotNumber }));
  }

  getSlotByRegistration(registrationNumber: string): number | null {
    const index: number = this.slots.findIndex(
      vehicle => vehicle && vehicle.registrationNumber === registrationNumber
    );

    return index !== -1 ? index : null;
  }

  getSlotsByColor(color: string): number[] {
    return this.slots
      .map((vehicle, index) => ({ vehicle, index }))
      .filter((item): item is { vehicle: Vehicle; index: number } => item.vehicle !== null && item.vehicle.color === color)
      .map(item => item.index);
  }

  getAvailableSlots(): number[] {
    return Array.from(this.availableSlots);
  }
}

