class Vehicle {
  registrationNumber;
  color;
  isHandicapped;

  constructor(registrationNumber, color, isHandicapped = false) {
    this.registrationNumber = registrationNumber;
    this.color = color;
    this.isHandicapped = isHandicapped;
  }
}

class ParkingLot {
  capacity;
  handicappedCapacity;
  slots;
  availableSlots;
  handicappedAvailableSlots;
  handicappedReservedSlots;

  constructor(capacity, handicappedCapacity = 0) {
    if (handicappedCapacity > capacity) {
      throw new Error("Handicapped capacity cannot exceed total capacity.");
    }
    this.capacity = capacity;
    this.handicappedCapacity = handicappedCapacity;
    this.slots = new Array(capacity).fill(null);
    this.availableSlots = new Set(); // Regular available slots
    this.handicappedAvailableSlots = new Set(); // Available handicapped slots
    this.handicappedReservedSlots = new Set(); // Fixed handicapped slot numbers

    for (let i = 0; i < capacity; i++) {
      if (i < handicappedCapacity) {
        this.handicappedAvailableSlots.add(i);
        this.handicappedReservedSlots.add(i);
      } else {
        this.availableSlots.add(i);
      }
    }
  }

  parkVehicle(vehicle) {
    let slotNumber = null;

    if (vehicle.isHandicapped) {
      if (this.handicappedAvailableSlots.size > 0) {
        slotNumber = this.handicappedAvailableSlots.values().next().value;
        this.handicappedAvailableSlots.delete(slotNumber);
      } else if (this.availableSlots.size > 0) {
        slotNumber = this.availableSlots.values().next().value;
        this.availableSlots.delete(slotNumber);
      }
    } else { // Regular vehicle
      if (this.availableSlots.size > 0) {
        slotNumber = this.availableSlots.values().next().value;
        this.availableSlots.delete(slotNumber);
      } else {
        return { success: false, message: "No regular or available handicapped slots for a non-handicapped vehicle" };
      }
    }

    if (slotNumber === null) {
      return { success: false, message: "No available parking slots" };
    }

    this.slots[slotNumber] = vehicle;

    return { 
      success: true, 
      message: `Vehicle parked successfully`, 
      slotNumber 
    };
  }

  removeVehicle(slotNumber) {
    if (slotNumber < 0 || slotNumber >= this.capacity) {
      return { success: false, message: "Invalid slot number" };
    }

    if (this.slots[slotNumber] === null) {
      return { success: false, message: "Slot is already empty" };
    }

    const vehicle = this.slots[slotNumber];
    this.slots[slotNumber] = null;

    if (this.handicappedReservedSlots.has(slotNumber)) {
      this.handicappedAvailableSlots.add(slotNumber);
    } else {
      this.availableSlots.add(slotNumber);
    }

    return { 
      success: true, 
      message: "Vehicle removed successfully", 
      vehicle 
    };
  }

  getVehiclesByColor(color) {
    return this.slots
      .filter(vehicle => vehicle && vehicle.color === color)
      .map((vehicle, index) => ({ vehicle, slotNumber: index }));
  }

  getSlotByRegistration(registrationNumber) {
    const index = this.slots.findIndex(
      vehicle => vehicle && vehicle.registrationNumber === registrationNumber
    );

    return index !== -1 ? index : null;
  }

  getSlotsByColor(color) {
    return this.slots
      .map((vehicle, index) => ({ vehicle, index }))
      .filter(item => item.vehicle && item.vehicle.color === color)
      .map(item => item.index);
  }

  getAvailableSlots() {
    return Array.from(this.availableSlots).concat(Array.from(this.handicappedAvailableSlots)).sort((a, b) => a - b);
  }

  getHandicappedSlots() {
    return Array.from(this.handicappedReservedSlots).sort((a, b) => a - b);
  }
}

// Example usage
const parkingLot = new ParkingLot(5, 2); // 5 total slots, 2 handicapped (slots 0, 1)

const regularCar1 = new Vehicle("R123", "Blue");
const regularCar2 = new Vehicle("R456", "Red");
const handicappedCar1 = new Vehicle("H789", "Green", true);
const handicappedCar2 = new Vehicle("H111", "Yellow", true);
const regularCar3 = new Vehicle("R222", "Black");

console.log("Initial available:", parkingLot.getAvailableSlots()); // Should include all 0-4
console.log("Handicapped reserved:", parkingLot.getHandicappedSlots()); // Should be [0, 1]

console.log(parkingLot.parkVehicle(regularCar1)); // Parks in 2
console.log(parkingLot.parkVehicle(regularCar2)); // Parks in 3
console.log(parkingLot.parkVehicle(handicappedCar1)); // Parks in 0 (handicapped slot)
console.log(parkingLot.parkVehicle(regularCar3)); // Parks in 4

console.log("After parking:");
console.log("Available slots:", parkingLot.getAvailableSlots()); // Only slot 1 (handicapped) should be available
console.log("Handicapped available slots (internal):", Array.from(parkingLot.handicappedAvailableSlots)); // Should be [1]

console.log(parkingLot.parkVehicle(handicappedCar2)); // Parks in 1

console.log("After all parking:");
console.log("Available slots:", parkingLot.getAvailableSlots()); // Should be empty

const regularCar4 = new Vehicle("R999", "White");
console.log(parkingLot.parkVehicle(regularCar4)); // Should fail: "No regular or available handicapped slots for a non-handicapped vehicle"

console.log(parkingLot.removeVehicle(0)); // Remove handicappedCar1
console.log("After removing handicappedCar1:");
console.log("Handicapped available slots (internal):", Array.from(parkingLot.handicappedAvailableSlots)); // Should be [0]
console.log("Available slots:", parkingLot.getAvailableSlots()); // Should include 0

console.log(parkingLot.parkVehicle(regularCar4)); // Should fail again as 0 is handicapped only

