import React, { useState, useEffect } from 'react';

const UserStatusIndicator = ({ url, pollingInterval = 5000 }) => {
  const [isOnline, setIsOnline] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Function to check user status
    async function checkStatus() {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          // Handle HTTP errors (e.g., 404, 500)
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        // Consider user online only if status code is 200
        setIsOnline(response.status === 200);
        setError(null);
      } catch (err) {
        // Handle network errors or errors thrown above
        setIsOnline(false);
        setError(err.message);
      }
    }

    // Check status immediately on mount
    checkStatus();

    // Set up polling interval
    const intervalId = setInterval(checkStatus, pollingInterval);

    // Clean up on unmount or before dependencies change
    return () => {
      console.log('Clearing interval:', intervalId);
      clearInterval(intervalId);
    };

  }, [url, pollingInterval]); // Dependencies ensure effect re-runs if url or interval changes

  // Loading state
  if (isOnline === null && !error) {
    return <div className="status-indicator loading">Checking status...</div>;
  }

  // Error state
  if (error) {
    return <div className="status-indicator error">Error: {error}</div>;
  }

  // Online/Offline state
  return (
    <div className={`status-indicator ${isOnline ? 'online' : 'offline'}`}>
      <div className="indicator-dot"></div>
      <span>{isOnline ? 'Online' : 'Offline'}</span>
    </div>
  );
};

export default UserStatusIndicator;

import React from 'react';

const FormField = ({ field, onChange, value }) => {
  const commonProps = {
    id: field.id,
    name: field.name,
    value: value || '',
    onChange: e => onChange(field.id, e.target.value),
    placeholder: field.placeholder,
    required: field.required,
    className: 'form-field-input'
  };

  switch (field.type) {
    case 'text':
    case 'email':
    case 'password':
    case 'number':
      return (
        <div className="form-group">
          <label htmlFor={field.id}>{field.label}</label>
          <input type={field.type} {...commonProps} />
        </div>
      );
    case 'textarea':
      return (
        <div className="form-group">
          <label htmlFor={field.id}>{field.label}</label>
          <textarea {...commonProps} rows={field.rows || 3} />
        </div>
      );
    case 'checkbox':
      // Checkbox uses `checked` and different onChange handling
      return (
        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id={field.id}
            name={field.name}
            checked={!!value} // Convert truthy/falsy to boolean
            onChange={e => onChange(field.id, e.target.checked)}
            required={field.required}
            className="form-field-checkbox"
          />
          <label htmlFor={field.id}>{field.label}</label>
        </div>
      );
    case 'select':
      return (
        <div className="form-group">
          <label htmlFor={field.id}>{field.label}</label>
          <select {...commonProps}>
            {field.options && field.options.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>
      );
    default:
      return (
        <div className="form-group error">
          <p>Unknown field type: {field.type}</p>
        </div>
      );
  }
};

export default FormField;

import React, { useState } from 'react';
import FormField from './FormField'; // Assume FormField is in a separate file

const DynamicForm = ({ schema, onSubmit }) => {
  // Initialize form data from schema or empty object
  const initialFormData = schema.reduce((acc, field) => {
    acc[field.id] = field.defaultValue || ''; // Initialize with default or empty string
    if (field.type === 'checkbox') acc[field.id] = !!field.defaultValue;
    return acc;
  }, {});

  const [formData, setFormData] = useState(initialFormData);

  const handleFieldChange = (id, value) => {
    setFormData(prevData => ({
      ...prevData,
      [id]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically add validation logic before submitting
    console.log('Form submitted with data:', formData);
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="dynamic-form">
      {schema.map(field => (
        <FormField
          key={field.id} // Essential for list rendering performance and correctness
          field={field}
          value={formData[field.id]}
          onChange={handleFieldChange}
        />
      ))}
      <button type="submit" className="submit-button">Submit Form</button>
    </form>
  );
};

export default DynamicForm;

