import React, { useState, useEffect } from 'react';

const UserStatusIndicator = ({ url, pollingInterval = 5000, retries = 3, retryDelay = 1000 }) => {
  const [isOnline, setIsOnline] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function checkStatusWithRetry(attempt = 1) {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          // Throw error for non-2xx status codes
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        setIsOnline(response.status === 200);
        setError(null);
      } catch (err) {
        console.error(`Attempt ${attempt} failed:`, err.message);
        if (attempt < retries) {
          // Retry after a delay
          setTimeout(() => {
            checkStatusWithRetry(attempt + 1);
          }, retryDelay);
        } else {
          // All retries failed
          setIsOnline(false);
          setError(`Failed after ${retries} attempts: ${err.message}`);
        }
      }
    }

    // Initial check and subsequent polling
    checkStatusWithRetry();
    const intervalId = setInterval(() => checkStatusWithRetry(), pollingInterval);

    return () => clearInterval(intervalId);
  }, [url, pollingInterval, retries, retryDelay]);

  if (isOnline === null && !error) {
    return <div className="status-indicator loading">Checking status...</div>;
  }
  if (error) {
    return <div className="status-indicator error">Error: {error}</div>;
  }
  return (
    <div className={`status-indicator ${isOnline ? 'online' : 'offline'}`}>
      <div className="indicator-dot"></div>
      <span>{isOnline ? 'Online' : 'Offline'}</span>
    </div>
  );
};

export default UserStatusIndicator;
