// DynamicForm.tsx
import React, { useState } from 'react';
import FormField from './FormField'; // Assume FormField is in a separate file

const DynamicForm = ({ schema, onSubmit, initialValues = {} }) => {
  // Helper to initialize nested objects recursively
  const initializeFormData = (s, currentValues = {}) => {
    return s.reduce((acc, field) => {
      if (field.type === 'object' && field.fields) {
        acc[field.id] = initializeFormData(field.fields, currentValues[field.id] || {});
      } else if (field.type === 'checkbox') {
        acc[field.id] = typeof currentValues[field.id] !== 'undefined' ? !!currentValues[field.id] : !!field.defaultValue;
      } else {
        acc[field.id] = typeof currentValues[field.id] !== 'undefined' ? currentValues[field.id] : (field.defaultValue || '');
      }
      return acc;
    }, {});
  };

  const [formData, setFormData] = useState(() => initializeFormData(schema, initialValues));

  // Function to deeply update nested state
  const setNestedValue = (obj, path, value) => {
    const newObj = { ...obj };
    let current = newObj;
    for (let i = 0; i < path.length - 1; i++) {
      const key = path[i];
      if (!current[key] || typeof current[key] !== 'object') {
        current[key] = {}; // Ensure path exists
      }
      current = current[key];
    }
    current[path[path.length - 1]] = value;
    return newObj;
  };

  const handleFieldChange = (path, value) => {
    // Path can be a string (e.g., 'name') or an array (e.g., ['address', 'street'])
    const fullPath = Array.isArray(path) ? path : [path];
    setFormData(prevData => setNestedValue(prevData, fullPath, value));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted with data:', formData);
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="dynamic-form">
      {schema.map(field => (
        <FormField
          key={field.id}
          field={field}
          value={formData[field.id]}
          // Pass current path for nested fields. If it's a top-level field, pass its ID.
          // If it's a nested form, it will append its own field IDs to this path.
          onChange={(fieldId, val) => handleFieldChange(fieldId, val)}
        />
      ))}
      <button type="submit" className="submit-button">Submit Form</button>
    </form>
  );
};

export default DynamicForm;

