import React, { useState, useEffect, useRef, useCallback } from 'react';

interface UserStatusIndicatorProps {
  url: string;
  pollingInterval?: number; // interval for regular successful polls
  maxRetries?: number; // max retry attempts on failure
  initialRetryDelay?: number; // initial delay for exponential backoff
}

const UserStatusIndicator: React.FC<UserStatusIndicatorProps> = ({
  url,
  pollingInterval = 5000,
  maxRetries = 5,
  initialRetryDelay = 1000,
}) => {
  const [isOnline, setIsOnline] = useState<boolean | null>(null);
  const [error, setError] = useState<string | null>(null);

  // Using useRef to store mutable timers without re-rendering
  const pollingTimerRef = useRef<NodeJS.Timeout | null>(null);
  const retryTimerRef = useRef<NodeJS.Timeout | null>(null);

  // Ref for mutable retry attempt counter (so it doesn't trigger effect re-runs)
  const currentAttemptRef = useRef(0);

  // Cleanup function for timers
  const clearTimers = useCallback(() => {
    if (pollingTimerRef.current) {
      clearInterval(pollingTimerRef.current);
      pollingTimerRef.current = null;
    }
    if (retryTimerRef.current) {
      clearTimeout(retryTimerRef.current);
      retryTimerRef.current = null;
    }
  }, []);

  // Use useCallback to memoize checkStatus, preventing unnecessary effect re-runs
  const checkStatus = useCallback(async () => {
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      setIsOnline(response.status === 200);
      setError(null);
      currentAttemptRef.current = 0; // Reset retry count on success

      clearTimers(); // Clear any existing timers (retry or previous poll)
      pollingTimerRef.current = setInterval(checkStatus, pollingInterval); // Schedule next regular poll

    } catch (err: any) {
      console.error('Fetch error:', err.message);
      currentAttemptRef.current++;

      if (currentAttemptRef.current <= maxRetries) {
        const delay = initialRetryDelay * Math.pow(2, currentAttemptRef.current - 1); // currentAttemptRef.current starts at 1 for first retry
        console.log(`Retrying in ${delay}ms (attempt ${currentAttemptRef.current}/${maxRetries})`);

        clearTimers(); // Clear polling timer before scheduling retry
        retryTimerRef.current = setTimeout(() => {
          checkStatus(); // Call self for retry
        }, delay);
      } else {
        // All retries failed
        setIsOnline(false);
        setError(`Failed to connect after ${maxRetries} attempts: ${err.message}`);
        clearTimers(); // Stop all timers definitively
      }
    }
  }, [url, pollingInterval, maxRetries, initialRetryDelay, clearTimers]);
  // The dependency array for useCallback ensures checkStatus is recreated only when these props change.

  useEffect(() => {
    // Initial check on mount
    checkStatus();

    // Cleanup function for useEffect to clear timers on unmount
    return clearTimers;
  }, [checkStatus, clearTimers]); // checkStatus and clearTimers are dependencies because they are defined outside

  // Loading state
  if (isOnline === null && !error) {
    return <div className="status-indicator loading">Checking status...</div>;
  }

  // Error state
  if (error) {
    return <div className="status-indicator error">Error: {error}</div>;
  }

  // Online/Offline state
  return (
    <div className={`status-indicator ${isOnline ? 'online' : 'offline'}`}>
      <div className="indicator-dot"></div>
      <span>{isOnline ? 'Online' : 'Offline'}</span>
    </div>
  );
};

export default UserStatusIndicator;

