// DynamicForm.tsx
import React, { useState } from 'react';
import FormField from './FormField'; 

interface FieldSchema {
  id: string;
  type: string;
  label: string;
  required?: boolean;
  minLength?: number;
  maxLength?: number;
  placeholder?: string;
  rows?: number;
  options?: { label: string; value: string }[];
  pattern?: string;
  defaultValue?: any;
}

interface DynamicFormProps {
  schema: FieldSchema[];
  onSubmit: (data: { [key: string]: any }) => void;
}

const DynamicForm: React.FC<DynamicFormProps> = ({ schema, onSubmit }) => {
  const initialFormData = schema.reduce((acc, field) => {
    acc[field.id] = field.defaultValue || (field.type === 'checkbox' ? false : '');
    if (field.type === 'select' && field.options && field.options.length > 0 && field.options[0].value === '') {
      acc[field.id] = ''; // Ensure default empty value for select if first option is empty
    }
    return acc;
  }, {});

  const [formData, setFormData] = useState<{ [key: string]: any }>(initialFormData);
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const handleFieldChange = (id: string, value: any) => {
    setFormData(prevData => ({
      ...prevData,
      [id]: value,
    }));
    // Clear error for this field immediately on change
    setErrors(prevErrors => {
      const newErrors = { ...prevErrors };
      delete newErrors[id];
      return newErrors;
    });
  };

  const validateForm = () => {
    let newErrors: { [key: string]: string } = {};
    let isValid = true;

    schema.forEach(field => {
      const value = formData[field.id];

      // Required check
      if (field.required) {
        if (field.type === 'checkbox' && value === false) {
          newErrors[field.id] = `${field.label} is required.`;
          isValid = false;
        } else if (typeof value === 'string' && value.trim() === '') {
          newErrors[field.id] = `${field.label} is required.`;
          isValid = false;
        } else if (value === null || typeof value === 'undefined') {
          newErrors[field.id] = `${field.label} is required.`;
          isValid = false;
        }
      }

      // minLength check for string-based inputs
      if (field.minLength && typeof value === 'string' && value.length < field.minLength) {
        newErrors[field.id] = `${field.label} must be at least ${field.minLength} characters.`;
        isValid = false;
      }

      // Optional: Email format check (basic regex)
      if (field.type === 'email' && value && !/^[\w.-]+@[\w.-]+\.\w{2,4}$/i.test(value)) {
        newErrors[field.id] = `Please enter a valid email address.`;
        isValid = false;
      }

      // Optional: Pattern check (more generic regex)
      if (field.pattern && typeof value === 'string' && value && !new RegExp(field.pattern).test(value)) {
        newErrors[field.id] = `${field.label} format is invalid.`;
        isValid = false;
      }

    });

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Form submitted with data:', formData);
      onSubmit(formData);
    } else {
      console.log('Form has validation errors:', errors);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="dynamic-form">
      {schema.map(field => (
        <FormField
          key={field.id}
          field={field}
          value={formData[field.id]}
          onChange={handleFieldChange}
          error={errors[field.id]} // Pass error message to FormField
        />
      ))}
      <button type="submit" className="submit-button">Submit Form</button>
    </form>
  );
};

export default DynamicForm;

// FormField.tsx
import React from 'react';

interface FieldProps {
  id: string;
  type: string;
  label: string;
  required?: boolean;
  minLength?: number;
  placeholder?: string;
  rows?: number;
  options?: { label: string; value: string }[];
  pattern?: string;
}

interface FormFieldProps {
  field: FieldProps;
  onChange: (id: string, value: any) => void;
  value: any;
  error?: string;
}

const FormField: React.FC<FormFieldProps> = ({ field, onChange, value, error }) => {
  const commonInputProps = {
    id: field.id,
    name: field.id, // Use id as name for consistency
    placeholder: field.placeholder,
    required: field.required,
    className: `form-field-input ${error ? 'error-input' : ''}`
  };

  switch (field.type) {
    case 'text':
    case 'email':
    case 'password':
      return (
        <div className="form-group">
          <label htmlFor={field.id}>{field.label}</label>
          <input type={field.type} value={value || ''} onChange={e => onChange(field.id, e.target.value)} {...commonInputProps} />
          {error && <span className="error-message" aria-live="polite">{error}</span>}
        </div>
      );
    case 'textarea':
      return (
        <div className="form-group">
          <label htmlFor={field.id}>{field.label}</label>
          <textarea value={value || ''} onChange={e => onChange(field.id, e.target.value)} rows={field.rows || 3} {...commonInputProps} />
          {error && <span className="error-message" aria-live="polite">{error}</span>}
        </div>
      );
    case 'checkbox':
      return (
        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id={field.id}
            name={field.id}
            checked={!!value}
            onChange={e => onChange(field.id, e.target.checked)}
            required={field.required}
            className={`form-field-checkbox ${error ? 'error-input' : ''}`}
          />
          <label htmlFor={field.id}>{field.label}</label>
          {error && <span className="error-message" aria-live="polite">{error}</span>}
        </div>
      );
    case 'select':
      return (
        <div className="form-group">
          <label htmlFor={field.id}>{field.label}</label>
          <select value={value || ''} onChange={e => onChange(field.id, e.target.value)} {...commonInputProps}>
            {field.options && field.options.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
          {error && <span className="error-message" aria-live="polite">{error}</span>}
        </div>
      );
    default:
      return (
        <div className="form-group error">
          <p>Unknown field type: {field.type}</p>
        </div>
      );
  }
};

export default FormField;

