        >
          <option value="">Select an option</option>
          {field.options && field.options.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      );
    case 'checkbox':
      return (
        <input
          type="checkbox"
          id={field.id}
          name={field.name}
          checked={value || false}
          onChange={e => onChange(field.id, e.target.checked)}
