};

const FormGroup = ({ group, values, onChange, errors }: any) => {
  const [expanded, setExpanded] = useState(true);

  if (group.groups) {
    return (
      <div className="form-group nested-group-container">
        <h3 onClick={() => setExpanded(!expanded)} className="group-header">
          {group.label} {expanded ? '' : ''}
