              <FormField
                field={field}
                value={values[field.id]}
                onChange={onChange}
                error={errors[field.id]} // Pass error prop down
              />
              {field.hint && <span className="hint">{field.hint}</span>}
              {errors[field.id] && <span style={{ color: 'red' }}>{errors[field.id]}</span>} {/* Display error message */}
            </div>
          ))}
