interface DynamicFormProps {
  formSchema: FormSchema;
  onSubmit: (values: { [key: string]: any }) => void;
}

const DynamicForm = ({ formSchema, onSubmit }: DynamicFormProps) => {
  const [formValues, setFormValues] = useState<{ [key: string]: any }>({});
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const handleFieldChange = (fieldId: string, value: any) => {
    setFormValues(prevValues => ({
      ...prevValues,
      [fieldId]: value
    }));
    // Clear error for this field as soon as it's modified
    setErrors(prevErrors => {
      const newErrors = { ...prevErrors };
      delete newErrors[fieldId];
      return newErrors;
    });
  };

  const collectRequiredFields = (schema: FormSchema | FormGroupConfig): FormFieldConfig[] => {
    let requiredFields: FormFieldConfig[] = [];
    if ((schema as FormGroupConfig).fields) {
      requiredFields = requiredFields.concat((schema as FormGroupConfig).fields!.filter(f => f.required));
    }
    if ((schema as FormGroupConfig).groups) {
      (schema as FormGroupConfig).groups!.forEach(group => {
        requiredFields = requiredFields.concat(collectRequiredFields(group));
      });
    }
    return requiredFields;
  };

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();

    const newErrors: { [key: string]: string } = {};
    const allRequiredFields = collectRequiredFields(formSchema);

    allRequiredFields.forEach(field => {
      const value = formValues[field.id];
      let isValid = true;
      if (field.type === 'checkbox') {
        isValid = value === true; // Checkbox must be explicitly true if required
      } else {
        isValid = !!value; // Check for any truthy value for other types
      }

      if (!isValid) {
        newErrors[field.id] = `${field.label} is required`;
      }
    });

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setErrors({});
      onSubmit(formValues);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="dynamic-form-container">
      <h2>{formSchema.label}</h2>
      {formSchema.groups.map(group => (
        <FormGroup
          key={group.id}
          group={group}
          values={formValues}
          onChange={handleFieldChange}
          errors={errors}
        />
      ))}
      <button type="submit">Submit Form</button>
    </form>
  );
};

export default DynamicForm;

