class Spreadsheet {
  constructor(rows = 5, cols = 5) {
    this.rows = rows;
    this.cols = cols;
    this.data = Array(rows).fill().map(() => Array(cols).fill(''));
    this.container = null; // Reference to the DOM element where spreadsheet will be rendered
  }

  render(containerElement) {
    this.container = containerElement;
    this.container.innerHTML = ''; // Clear previous content

    const table = document.createElement('table');
    table.className = 'spreadsheet';

    // Create table header (column letters)
    const thead = document.createElement('thead');
    const headerRow = document.createElement('tr');

    // Empty cell for row numbers column
    headerRow.appendChild(document.createElement('th'));

    // Column headers (A, B, C, etc.)
    for (let col = 0; col < this.cols; col++) {
      const th = document.createElement('th');
      th.textContent = String.fromCharCode(65 + col); // A, B, C, ...
      headerRow.appendChild(th);
    }

    // Add "+" button for adding columns
    const addColHeader = document.createElement('th');
    const addColButton = document.createElement('button');
    addColButton.textContent = '+';
    addColButton.addEventListener('click', () => this.addColumn());
    addColHeader.appendChild(addColButton);
    headerRow.appendChild(addColHeader);
    thead.appendChild(headerRow);
    table.appendChild(thead);

    // Create table body with data
    const tbody = document.createElement('tbody');

    for (let row = 0; row < this.rows; row++) {
      const tr = document.createElement('tr');

      // Row number
      const rowHeader = document.createElement('th');
      rowHeader.textContent = row + 1;
      tr.appendChild(rowHeader);

      // Row cells
      for (let col = 0; col < this.cols; col++) {
        const td = document.createElement('td');
        const input = document.createElement('input');
        input.value = this.data[row][col];
        input.dataset.row = row;
        input.dataset.col = col;

        input.addEventListener('change', (e) => {
          const { row, col } = e.target.dataset;
          this.data[parseInt(row)][parseInt(col)] = e.target.value;
          console.log(`Data updated at [${row},${col}]: ${e.target.value}`);
        });

        td.appendChild(input);
        tr.appendChild(td);
      }

      // Add "+" button for adding rows (optional, usually at the bottom)
      // For this example, we'll put it at the end of each row for simplicity
      // Or better, a single button at the bottom of the last row header

      tbody.appendChild(tr);
    }

    // Add a row for the 'add row' button if not already in existing rows
    const addRowTR = document.createElement('tr');
    const addRowTH = document.createElement('th');
    const addRowButton = document.createElement('button');
    addRowButton.textContent = '+';
    addRowButton.addEventListener('click', () => this.addRow());
    addRowTH.appendChild(addRowButton);
    addRowTR.appendChild(addRowTH);
    // Fill the rest of the row with empty cells or colspan
    const emptyTd = document.createElement('td');
    emptyTd.colSpan = this.cols + 1; // +1 for the addColHeader
    addRowTR.appendChild(emptyTd);
    tbody.appendChild(addRowTR);

    table.appendChild(tbody);
    this.container.appendChild(table);
  }

  addColumn() {
    this.cols++;
    for(let i = 0; i < this.rows; i++) {
      this.data[i].push(''); // Add a new empty cell to each row
    }
    console.log('Column added. New dimensions:', this.rows, 'x', this.cols);
    this.render(this.container); // Re-render the spreadsheet
  }

  addRow() {
    this.rows++;
    this.data.push(Array(this.cols).fill('')); // Add a new empty row
    console.log('Row added. New dimensions:', this.rows, 'x', this.cols);
    this.render(this.container); // Re-render the spreadsheet
  }
}

