import React, { useState, useCallback } from 'react';

const FormGroup = ({ group, values, onChange, onBlur, errors }) => {
  return (
    <fieldset>
      <legend>{group.title}</legend>
      {group.fields.map(field => (
        <div key={field.id} className="form-field">
          <label htmlFor={field.id}>{field.label}:</label>
          {field.type === 'checkbox' ? (
            <input
              id={field.id}
              type="checkbox"
              checked={!!values[field.id]}
              onChange={(e) => onChange(field.id, e.target.checked)}
              onBlur={() => onBlur(field.id, values[field.id], field.validation)}
            />
          ) : (
            <input
              id={field.id}
              type={field.type || 'text'}
              value={values[field.id] || ''}
              onChange={(e) => onChange(field.id, e.target.value)}
              onBlur={() => onBlur(field.id, values[field.id], field.validation)}
            />
          )}
          {errors[field.id] && <span className="error-message" style={{ color: 'red', fontSize: '0.8em' }}>{errors[field.id]}</span>}
        </div>
      ))}
    </fieldset>
  );
};

const DynamicForm = ({ formSchema, onSubmit }) => {
  const [formValues, setFormValues] = useState({});
  const [formErrors, setFormErrors] = useState({});

  // Helper function to find a field by its ID in the schema
  const findFieldInSchema = useCallback((fieldId) => {
    for (const group of formSchema.groups) {
      const field = group.fields.find(f => f.id === fieldId);
      if (field) return field;
    }
    return null;
  }, [formSchema]);

  const validateField = useCallback((fieldId, value, validationRules) => {
    if (!validationRules) return null; // No validation rules defined

    if (validationRules.required && (value === null || value === '' || (typeof value === 'boolean' && !value))) {
      return `${findFieldInSchema(fieldId)?.label || fieldId} is required.`;
    }
    if (validationRules.minLength && typeof value === 'string' && value.length < validationRules.minLength) {
      return `${findFieldInSchema(fieldId)?.label || fieldId} must be at least ${validationRules.minLength} characters.`;
    }
    if (validationRules.pattern && typeof value === 'string' && !validationRules.pattern.test(value)) {
      return `Invalid ${findFieldInSchema(fieldId)?.label || fieldId} format.`;
    }
    // Add more validation rules as needed (e.g., maxLength, min, max, custom functions)
    return null; // Valid
  }, [findFieldInSchema]);

  const handleFieldChange = (fieldId, value) => {
    setFormValues(prevValues => ({
      ...prevValues,
      [fieldId]: value,
    }));
    // Clear error for this field immediately on change, then re-validate on blur
    setFormErrors(prevErrors => ({ ...prevErrors, [fieldId]: null }));
  };

  const handleFieldBlur = (fieldId, value, validationRules) => {
    const error = validateField(fieldId, value, validationRules);
    setFormErrors(prevErrors => ({ ...prevErrors, [fieldId]: error }));
  };

  const validateForm = () => {
    let newErrors = {};
    let isValid = true;

    formSchema.groups.forEach(group => {
      group.fields.forEach(field => {
        const value = formValues[field.id];
        const error = validateField(field.id, value, field.validation);
        if (error) {
          newErrors[field.id] = error;
          isValid = false;
        }
      });
    });
    setFormErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validateForm();
    if (isValid) {
      console.log('Form submitted with values:', formValues);
      onSubmit(formValues);
    } else {
      console.log('Form has validation errors:', formErrors);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="dynamic-form">
      <h2>{formSchema.title}</h2>
      {formSchema.description && (
        <p className="form-description">{formSchema.description}</p>
      )}

      {formSchema.groups.map(group => (
        <FormGroup
          key={group.id}
          group={group}
          values={formValues}
          onChange={handleFieldChange}
          onBlur={handleFieldBlur} // Pass onBlur handler
          errors={formErrors}
        />
      ))}

      <div className="form-actions">
        <button type="submit" className="submit-button">
          {formSchema.submitText || 'Submit'}
        </button>
      </div>
    </form>
  );
};

export default DynamicForm;

