            e.target.value = this.formulas[cellId] ? this.evaluateCell(row, col) : this.data[row][col];
        });

        td.appendChild(input);
        tr.appendChild(td);
      }
      tbody.appendChild(tr);
    }
    table.appendChild(tbody);
    this.container.appendChild(table);
  }

  // Helper to convert 'A1' to [0,0]
  getCellCoords(cellRef) {
    const colStr = cellRef.match(/[A-Z]+/)[0];
    const rowStr = cellRef.match(/\d+/)[0];
    const col = colStr.charCodeAt(0) - 65;
    const row = parseInt(rowStr) - 1;
    if (row < 0 || row >= this.rows || col < 0 || col >= this.cols) {
      return null; // Invalid reference
    }
    return [row, col];
  }

  // Recursive function to get a cell's value (evaluating if it's a formula)
  getCellValue(row, col, visited = new Set()) {
    const cellId = `${row}_${col}`;
    if (visited.has(cellId)) {
        // Circular dependency detected
        return '#CIRCULAR!'; // Or throw an error
    }
    visited.add(cellId);

    if (this.formulas[cellId]) {
        return this.evaluateFormulaExpression(this.formulas[cellId], visited);
    } else {
        const value = this.data[row][col];
        return !isNaN(parseFloat(value)) && isFinite(value) ? parseFloat(value) : value;
    }
  }

  evaluateFormulaExpression(formulaString, visited) {
    let expression = formulaString.substring(1); // Remove '=' prefix
    const cellRefs = expression.match(/[A-Z]+\d+/g) || [];
    let evaluatedExpression = expression;

    cellRefs.forEach(cellRef => {
      const coords = this.getCellCoords(cellRef);
      if (!coords) {
        evaluatedExpression = '#REF!'; // Invalid cell reference
        return; 
      }
      const [r, c] = coords;
      const cellValue = this.getCellValue(r, c, new Set(visited)); // Pass a new set for recursive call
      if (typeof cellValue === 'string' && cellValue.startsWith('#')) {
        evaluatedExpression = cellValue; // Propagate errors
        return;
      }
      evaluatedExpression = evaluatedExpression.replace(new RegExp(cellRef, 'g'), `(${cellValue})`);
    });

    if (evaluatedExpression.startsWith('#')) return evaluatedExpression; // Propagate #REF! or #CIRCULAR!

    try {
      const result = eval(evaluatedExpression); // DANGER: For interview only. Use proper parser normally.
      return isFinite(result) ? result : '#ERROR!'; // Handle NaN, Infinity
    } catch (e) {
      return '#ERROR!';
    }
  }

  evaluateCell(row, col) {
    const cellId = `${row}_${col}`;
    if (this.formulas[cellId]) {
        return this.evaluateFormulaExpression(this.formulas[cellId], new Set());
    } else {
        return this.data[row][col];
    }
  }

  recalculateAll() {
    // A simple, inefficient recalculation. Re-render will cause all cells to re-evaluate.
    this.render(this.container);
  }

  addColumn() { /* ... unchanged ... */ }
  addRow() { /* ... unchanged ... */ }
  removeRow(rowIndex) { /* ... unchanged ... */ }
  removeColumn(colIndex) { /* ... unchanged ... */ }
}

