import React, { useState, useEffect, useRef } from 'react';

// Sample user data
const users = [
  { id: 1, name: 'John Smith', purchased: ['Laptop', 'Mouse'], 
    address: '123 Main St', zipCode: '10001' },
  { id: 2, name: 'Sarah Johnson', purchased: ['Phone', 'Headphones'], 
    address: '456 Oak Ave', zipCode: '20002' }
];

// Minimal custom useDebounce for this example
function useDebounce(value, delay) {
  const [debouncedValue, setDebouncedValue] = useState(value);
  useEffect(() => {
    const handler = setTimeout(() => { setDebouncedValue(value); }, delay);
    return () => { clearTimeout(handler); };
  }, [value, delay]);
  return debouncedValue;
}

function UserSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const searchInputRef = useRef(null);
  const resultsContainerRef = useRef(null);
  const dropdownId = 'user-search-results';

  const debouncedSearchTerm = useDebounce(searchTerm, 300);

  const searchUsers = (query) => {
    if (!query.trim()) return [];
    const lowerCaseQuery = query.toLowerCase();
    return users.filter(user => 
      user.name.toLowerCase().includes(lowerCaseQuery) ||
      user.purchased.some(item => item.toLowerCase().includes(lowerCaseQuery)) ||
      user.address.toLowerCase().includes(lowerCaseQuery) ||
      user.zipCode.includes(lowerCaseQuery)
    );
  };

  useEffect(() => {
    const filteredResults = searchUsers(debouncedSearchTerm);
    setSearchResults(filteredResults);
    setSelectedIndex(filteredResults.length > 0 ? 0 : -1);
    setIsDropdownOpen(debouncedSearchTerm.trim().length > 0 && filteredResults.length > 0);
  }, [debouncedSearchTerm]); // Only re-run when debounced term changes

  // Omitted keyboard and click-outside effects for brevity in this specific example
  // (see theory for full implementations)

  return (
    <div style={{ position: 'relative', width: '300px', margin: 'auto' }}>
      <input
        type="text"
        placeholder="Search users..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onFocus={() => setIsDropdownOpen(searchTerm.trim().length > 0 && searchResults.length > 0)}
        ref={searchInputRef}
        id="user-search-input"
        role="combobox"
        aria-autocomplete="list"
        aria-controls={dropdownId}
        aria-expanded={isDropdownOpen}
        aria-activedescendant={selectedIndex !== -1 && searchResults[selectedIndex] ? `result-item-${searchResults[selectedIndex].id}` : undefined}
        style={{ width: '100%', padding: '8px' }}
      />
      {isDropdownOpen && searchResults.length > 0 && (
        <ul
          id={dropdownId}
          ref={resultsContainerRef}
          role="listbox"
          style={{
            listStyle: 'none', padding: '0', margin: '5px 0 0 0',
            border: '1px solid #ddd', maxHeight: '150px', overflowY: 'auto',
            position: 'absolute', width: '100%', zIndex: 1000, backgroundColor: 'white'
          }}
        >
          {searchResults.map((user, index) => (
            <li 
              key={user.id}
              id={`result-item-${user.id}`}
              role="option"
              aria-selected={index === selectedIndex}
              className={index === selectedIndex ? 'selected' : ''}
              onClick={() => {
                console.log('Clicked user:', user);
                setSearchTerm(user.name);
                setIsDropdownOpen(false);
              }}
              style={{ padding: '8px', background: index === selectedIndex ? '#f0f0f0' : 'white', cursor: 'pointer' }}
            >
              {user.name} ({user.address})
            </li>
          ))}
        </ul>
      )}
      {isDropdownOpen && searchResults.length === 0 && searchTerm.trim().length > 0 && (
        <div style={{ padding: '8px', border: '1px solid #ddd', marginTop: '5px', backgroundColor: 'white' }}>No results found.</div>
      )}
    </div>
  );
}

export default UserSearch;

import React, { useState, useEffect, useCallback } from 'react';

function useKeyboardNavigation(items, onSelect) {
  const [selectedIndex, setSelectedIndex] = useState(-1);

  useEffect(() => {
    if (items.length > 0 && selectedIndex === -1) {
      setSelectedIndex(0); // Auto-select first item if results appear
    }
  }, [items]);

