import React, { useRef, useEffect } from 'react';

function MyComponent() {
  const inputRef = useRef(null); // Use case 1: Accessing DOM element
  const intervalIdRef = useRef(null); // Use case 2: Storing mutable value

  useEffect(() => {
    // 1. Focus on the input when the component mounts
    if (inputRef.current) {
      inputRef.current.focus();
    }

    // 2. Store an interval ID that won't trigger re-renders
    intervalIdRef.current = setInterval(() => {
      console.log('Interval running...');
    }, 1000);

    return () => {
      // Cleanup the interval on component unmount
      clearInterval(intervalIdRef.current);
    };
  }, []); // Empty dependency array means this effect runs once on mount and cleans up on unmount

  return (
    <div>
      <input ref={inputRef} type="text" placeholder="I will be focused!" />
      <p>Check console for interval messages.</p>
    </div>
  );
}
