];

// Custom hook for debouncing a value
function useDebounce(value, delay) {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

function UserSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(-1); // -1 for no selection initially
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const searchInputRef = useRef(null);
  const resultsContainerRef = useRef(null);
  const dropdownId = 'user-search-results'; // For ARIA attributes

  const debouncedSearchTerm = useDebounce(searchTerm, 300);

  // Search logic memoized with useCallback (users array is stable here)
  const performSearch = useCallback((query) => {
    if (!query.trim()) return [];
    const lowerCaseQuery = query.toLowerCase();

    return users.filter(user => {
      const nameMatch = user.name.toLowerCase().includes(lowerCaseQuery);
      const purchasedMatch = user.purchased.some(item => item.toLowerCase().includes(lowerCaseQuery));
      const addressMatch = user.address.toLowerCase().includes(lowerCaseQuery);
      const zipCodeMatch = user.zipCode.includes(lowerCaseQuery);
      return nameMatch || purchasedMatch || addressMatch || zipCodeMatch;
    });
  }, [users]); // 'users' is stable, but include if it could change

  // Effect to perform search when debounced term changes
  useEffect(() => {
    const filtered = performSearch(debouncedSearchTerm);
    setSearchResults(filtered);
    setSelectedIndex(filtered.length > 0 ? 0 : -1); // Auto-select first if results exist
    setIsDropdownOpen(debouncedSearchTerm.trim().length > 0 && filtered.length > 0); // Open if term and results
  }, [debouncedSearchTerm, performSearch]);

  // Keyboard navigation logic
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!isDropdownOpen || searchResults.length === 0) return;

      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          setSelectedIndex(prevIndex => (prevIndex + 1) % searchResults.length);
          break;
        case 'ArrowUp':
          e.preventDefault();
          setSelectedIndex(prevIndex => (prevIndex - 1 + searchResults.length) % searchResults.length);
          break;
        case 'Enter':
          e.preventDefault();
          if (selectedIndex !== -1 && selectedIndex < searchResults.length) {
            const selectedUser = searchResults[selectedIndex];
            console.log('Selected user:', selectedUser);
            setSearchTerm(selectedUser.name); // Update input field with selected name
            setIsDropdownOpen(false); // Close dropdown
            searchInputRef.current?.focus(); // Return focus to input for continued typing
          }
          break;
        case 'Escape':
          setIsDropdownOpen(false);
          searchInputRef.current?.focus(); // Return focus to input
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [searchResults, selectedIndex, isDropdownOpen]);

  // Scroll selected item into view
  useEffect(() => {
    if (resultsContainerRef.current && selectedIndex !== -1) {
      const selectedElement = resultsContainerRef.current.children[selectedIndex];
      if (selectedElement) {
        selectedElement.scrollIntoView({
          behavior: 'smooth',
          block: 'nearest'
        });
      }
    }
  }, [selectedIndex, searchResults]); // Dependency on searchResults ensures it runs if results change while an item is selected

  // Close dropdown on click outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchInputRef.current && !searchInputRef.current.contains(event.target) &&
          resultsContainerRef.current && !resultsContainerRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div style={{ position: 'relative', width: '400px', margin: '50px auto', fontFamily: 'Arial, sans-serif' }}>
      <input
        type="text"
        placeholder="Search for users..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onFocus={() => setIsDropdownOpen(searchTerm.trim().length > 0 && searchResults.length > 0)}
        ref={searchInputRef}
        id="user-search-input"
        role="combobox"
        aria-autocomplete="list"
        aria-controls={dropdownId}
        aria-expanded={isDropdownOpen}
        // aria-activedescendant points to the ID of the currently selected option
        aria-activedescendant={selectedIndex !== -1 && isDropdownOpen && searchResults[selectedIndex] ? `result-item-${searchResults[selectedIndex].id}` : undefined}
        style={{ width: '100%', padding: '10px', fontSize: '16px', border: '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box' }}
      />
      {isDropdownOpen && searchResults.length > 0 && (
        <ul
          id={dropdownId}
          ref={resultsContainerRef}
          role="listbox"
          style={{
            listStyle: 'none',
            padding: '0',
            margin: '5px 0 0 0',
            border: '1px solid #ddd',
            maxHeight: '250px',
            overflowY: 'auto',
            position: 'absolute',
            width: '100%',
            zIndex: 1000,
            backgroundColor: 'white',
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
            borderRadius: '4px'
          }}
        >
          {searchResults.map((user, index) => (
            <li
              key={user.id}
              id={`result-item-${user.id}`}
              role="option"
