import React, { useEffect, useRef, useReducer } from 'react';

const MOCK_USERS = [
  { id: 1, name: 'Alice Smith', purchased: ['Laptop', 'Mouse'], address: '123 Main St', zipCode: '10001' },
  { id: 2, name: 'Bob Johnson', purchased: ['Keyboard'], address: '456 Oak Ave', zipCode: '20002' },
  { id: 3, name: 'Charlie Brown', purchased: ['Monitor', 'Webcam'], address: '789 Pine Ln', zipCode: '30003' },
  { id: 4, name: 'Diana Prince', purchased: ['Headphones'], address: '101 Elm Blvd', zipCode: '10001' },
  { id: 5, name: 'Eve Adams', purchased: ['Desk'], address: '202 River Rd', zipCode: '40004' },
  { id: 6, name: 'Frank White', purchased: ['Chair'], address: '303 Hill Dr', zipCode: '50005' }
];

const initialState = {
  searchTerm: '',
  searchResults: [],
  selectedIndex: -1, // Changed to -1 to indicate no selection initially
  isDropdownOpen: false,
};

function searchReducer(state, action) {
  switch (action.type) {
    case 'SET_SEARCH_TERM':
      return { ...state, searchTerm: action.payload };
    case 'SET_SEARCH_RESULTS':
      return { ...state, searchResults: action.payload };
    case 'SET_SELECTED_INDEX':
      return { ...state, selectedIndex: action.payload };
    case 'SET_DROPDOWN_OPEN':
      return { ...state, isDropdownOpen: action.payload };
    case 'NAVIGATE_DOWN':
      return {
        ...state,
        selectedIndex: (state.selectedIndex < state.searchResults.length - 1)
          ? state.selectedIndex + 1
          : state.selectedIndex,
      };
    case 'NAVIGATE_UP':
      return {
        ...state,
        selectedIndex: (state.selectedIndex > 0) ? state.selectedIndex - 1 : 0,
      };
    case 'SELECT_ITEM':
      // Log selected user and reset state
      console.log('Selected user:', action.payload);
      return { ...initialState }; // Reset to initial state after selection
    case 'CLEAR_SEARCH':
      return { ...initialState };
    default:
      return state;
  }
}

function UserSearchComponent() {
  const [state, dispatch] = useReducer(searchReducer, initialState);
  const { searchTerm, searchResults, selectedIndex, isDropdownOpen } = state;

  const searchInputRef = useRef(null);
  const resultsContainerRef = useRef(null);

  const searchUsers = (query) => {
    if (!query) return [];
    const lowerCaseQuery = query.toLowerCase();
    return MOCK_USERS.filter(user => {
      if (user.name.toLowerCase().includes(lowerCaseQuery)) return true;
      if (user.purchased.some(item => item.toLowerCase().includes(lowerCaseQuery))) return true;
      if (user.address.toLowerCase().includes(lowerCaseQuery)) return true;
      if (user.zipCode.includes(lowerCaseQuery)) return true;
      return false;
    });
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    dispatch({ type: 'SET_SEARCH_TERM', payload: value });
    const results = searchUsers(value);
    dispatch({ type: 'SET_SEARCH_RESULTS', payload: results });
    dispatch({ type: 'SET_SELECTED_INDEX', payload: results.length > 0 ? 0 : -1 });
    dispatch({ type: 'SET_DROPDOWN_OPEN', payload: results.length > 0 });
  };

  const handleKeyDown = (e) => {
    if (!isDropdownOpen) return;

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        dispatch({ type: 'NAVIGATE_DOWN' });
        break;
      case 'ArrowUp':
        e.preventDefault();
        dispatch({ type: 'NAVIGATE_UP' });
        break;
      case 'Enter':
        e.preventDefault();
        if (selectedIndex !== -1 && searchResults[selectedIndex]) {
          dispatch({ type: 'SELECT_ITEM', payload: searchResults[selectedIndex] });
        }
        break;
      case 'Escape':
        e.preventDefault();
        dispatch({ type: 'SET_DROPDOWN_OPEN', payload: false });
        break;
      default:
        break;
    }
  };

  useEffect(() => {
    if (isDropdownOpen && resultsContainerRef.current && selectedIndex !== -1) {
      const selectedElement = document.getElementById(`user-item-${selectedIndex}`);
      if (selectedElement) {
        selectedElement.scrollIntoView({ block: 'nearest', behavior: 'smooth' });
      }
    }
  }, [selectedIndex, isDropdownOpen]);

  const handleSelectUser = (user) => {
    dispatch({ type: 'SELECT_ITEM', payload: user });
  };

  return (
    <div className="user-search">
      <div className="search-container">
        <input
          ref={searchInputRef}
          type="text"
          placeholder="Search users by name, items, address..."
          value={searchTerm}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          onFocus={() => dispatch({ type: 'SET_DROPDOWN_OPEN', payload: searchResults.length > 0 })}
        />

        {isDropdownOpen && (
          <div className="search-results" ref={resultsContainerRef}>
            {searchResults.length > 0 ? (
              searchResults.map((user, index) => (
                <div
                  id={`user-item-${index}`}
                  key={user.id}
                  className={`user-item ${index === selectedIndex ? 'selected' : ''}`}
                  onClick={() => handleSelectUser(user)}
                  onMouseEnter={() => dispatch({ type: 'SET_SELECTED_INDEX', payload: index })}
                >
                  <div className="user-name">{user.name}</div>
                </div>
              ))
            ) : (
              <div className="no-results">No results found.</div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default UserSearchComponent;

