  { id: 6, name: 'Frank White', purchased: ['Chair'], address: '303 Hill Dr', zipCode: '50005' },
  { id: 7, name: 'Grace Taylor', purchased: ['Tablet'], address: '404 Creek Rd', zipCode: '60006' }
];

// Helper for basic fuzzy match (1 character difference allowed)
const isFuzzyMatch = (text, query) => {
  if (text.includes(query)) return true;
  if (query.length < 2) return false; // Needs at least 2 chars for meaningful fuzzy

  // Allows for one character difference (insertion, deletion, substitution)
  for (let i = 0; i <= text.length; i++) {
    // Insertion
    if (text.slice(0, i) + query + text.slice(i) === query) return true; // not needed, just for conceptual clarity
  }

  // Simple 1-char difference check:
  // Check if query can be formed by adding/removing/changing one char in text
  if (Math.abs(text.length - query.length) > 1) return false; // Too many differences

  let diffCount = 0;
  let tIdx = 0;
  let qIdx = 0;

  while (tIdx < text.length && qIdx < query.length) {
    if (text[tIdx] !== query[qIdx]) {
      diffCount++;
      if (diffCount > 1) return false;

      // Try skipping a char in text (deletion in query)
      if (text.length > query.length) {
        tIdx++;
      } else if (query.length > text.length) {
        // Try skipping a char in query (insertion in query)
        qIdx++;
      } else {
        // Substitution
        tIdx++;
        qIdx++;
      }
    } else {
      tIdx++;
      qIdx++;
    }
  }
  diffCount += Math.abs((text.length - tIdx) - (query.length - qIdx));

  return diffCount <= 1;
};


function UserSearchComponent() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const searchInputRef = useRef(null);
  const resultsContainerRef = useRef(null);

  const searchUsers = (query) => {
    if (!query) return [];
    const lowerCaseKeywords = query.toLowerCase().split(' ').filter(k => k.length > 0);

    if (lowerCaseKeywords.length === 0) return [];

    return MOCK_USERS.filter(user => {
      // Combine all searchable fields into a single string for each user
      const userSearchableText = (
        user.name + ' ' +
        user.address + ' ' +
        user.zipCode + ' ' +
        user.purchased.join(' ')
      ).toLowerCase();

      // Check if ALL keywords are present in the user's searchable text (or fuzzy match)
      return lowerCaseKeywords.every(keyword => {
        // Original exact match
        if (userSearchableText.includes(keyword)) return true;
        // Fuzzy match for name and address fields
        const nameAndAddress = (user.name + ' ' + user.address).toLowerCase();
        return isFuzzyMatch(nameAndAddress, keyword); // Apply fuzzy match only to relevant fields
      });
