```typescript
import React, { useEffect, useRef, useState } from 'react';

interface LazyImageProps {
  src: string;
  alt: string;
  placeholder?: string; // Optional placeholder image URL
  className?: string;
  style?: React.CSSProperties;
}

const LazyImage: React.FC<LazyImageProps> = ({
  src,
  alt,
  placeholder = 'data:image/gif;base64,R0lGODlhAQABAAD/ACwAAAAAAQABAAACADs=', // Tiny transparent GIF as default placeholder
  className,
  style
}) => {
  const [imageSrc, setImageSrc] = useState(placeholder);
  const [isLoaded, setIsLoaded] = useState(false);
  const imgRef = useRef<HTMLImageElement>(null);

  useEffect(() => {
    if (!imgRef.current) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting && !isLoaded) {
            setImageSrc(src); // Start loading the actual image
            // No setIsLoaded(true) here yet, as image might still be loading
            observer.unobserve(entry.target); // Stop observing once trigger to load
          }
        });
      },
      { rootMargin: '100px 0px' } // Load when 100px from viewport
    );

    observer.observe(imgRef.current);

    // Cleanup function
    return () => {
      if (imgRef.current) {
        observer.unobserve(imgRef.current);
      }
    };
  }, [src, isLoaded]); // Depend on src changing and isLoaded state for effect re-runs

  const handleImageLoad = () => {
    setIsLoaded(true);
  };

  return (
    <img
      ref={imgRef}
      src={imageSrc}
      alt={alt}
      onLoad={handleImageLoad}
      className={className}
      style={{
        ...style,
        opacity: isLoaded ? 1 : 0.7, // Visual feedback for loading
        transition: 'opacity 0.3s ease-in-out',
      }}
    />
  );
};

export default LazyImage;
```

