function calculateHtmlMinification(htmlString) {
  let minifiedHtml = htmlString;

  // 1. Remove HTML comments (multiline and single line)
  minifiedHtml = minifiedHtml.replace(/<!--[\s\S]*?-->/g, '');

  // 2. Remove whitespace between HTML tags
  // This ensures that `<div>   <p>` becomes `<div><p>`
  minifiedHtml = minifiedHtml.replace(/>\s+</g, '><');

  // 3. Collapse multiple whitespace characters (including newlines and tabs) to a single space.
  // This applies to whitespace within text nodes but also removes unnecessary spaces.
  // This regex carefully avoids collapsing spaces within attribute values (e.g. title="my title")
  // For simplicity in a regex-based approach, we'll mostly target spaces not within angle brackets.
  // A common approach for production minifiers is to use an HTML parser.
  // For this task, we can use a simpler, effective regex for common cases:
  minifiedHtml = minifiedHtml.replace(/\s\s+/g, ' '); // Replace 2+ spaces with a single space

  // 4. Trim leading/trailing whitespace from the entire document
  minifiedHtml = minifiedHtml.trim();

  // Calculate sizes in bytes using TextEncoder for accurate byte length
  const originalSize = new TextEncoder().encode(htmlString).length;
  const minifiedSize = new TextEncoder().encode(minifiedHtml).length;

  const reductionPercentage = ((originalSize - minifiedSize) / originalSize) * 100;

  return {
    minifiedHtml,
    originalSize,
    minifiedSize,
    reductionPercentage: isNaN(reductionPercentage) || originalSize === 0 ? 0 : parseFloat(reductionPercentage.toFixed(2))
  };
}

