import React, { useState, useEffect, useCallback } from 'react';
import './App.css'; // Assume App.css provides basic styling

// --- 1. Domain Layer: Product Model --- (Implicit in interface, explicit if complex validation needed)
interface Product {
  id: number;
  title: string;
  price: number;
  description: string;
  category: string;
  image: string;
}

// --- 2. Infrastructure Layer: productApi Service ---
const productApi = {
  fetchProducts: async (limit: number = 5): Promise<Product[]> => {
    const response = await fetch(`https://fakestoreapi.com/products?limit=${limit}`);
    if (!response.ok) {
      throw new Error(`Failed to fetch products: ${response.statusText}`);
    }
    return response.json();
  },
};

// --- 3. Application Layer: useProducts Custom Hook ---
const useProducts = (limit?: number) => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadProducts = async () => {
      setLoading(true);
      setError(null);
      try {
        const data = await productApi.fetchProducts(limit);
        setProducts(data);
      } catch (e: any) {
        setError(e.message);
      } finally {
        setLoading(false);
      }
    };
    loadProducts();
  }, [limit]); // Re-fetch if limit changes

  return { products, loading, error };
};

// --- 4. UI Layer: Presentational Components ---

interface ProductCardProps {
  product: Product;
}

const ProductCard: React.FC<ProductCardProps> = React.memo(({ product }) => {
  console.log(`Rendering ProductCard: ${product.title}`);
