      <img src={product.image} alt={product.title} style={{ width: '100px', height: '100px', objectFit: 'cover' }} />
      <h3>{product.title}</h3>
      <p>${product.price.toFixed(2)}</p>
      <p>{product.description.substring(0, 70)}...</p>
    </div>
  );
});

interface ProductListProps {
  products: Product[];
}

const ProductList: React.FC<ProductListProps> = React.memo(({ products }) => {
  console.log('Rendering ProductList');
  return (
    <div className="product-list-container">
      {products.map((product) => (
        <ProductCard key={product.id} product={product} />
      ))}
    </div>
  );
});

// --- 5. Main Application Orchestration --- (App Component)
function App() {
  const { products, loading, error } = useProducts(5); // Fetch 5 products

  if (loading) return <div className="app-status">Loading products...</div>;
  if (error) return <div className="app-status">Error: {error}</div>;

  return (
    <div className="App">
      <h1>Our Products (Refactored)</h1>
      <ProductList products={products} />
    </div>
  );
}

export default App;

