import React, { useRef, useEffect, useState } from 'react';

interface LazyImageProps {
  src: string;
  placeholder: string;
  alt: string;
  errorPlaceholder?: string;
}

function LazyImage({ src, placeholder, alt, errorPlaceholder }: LazyImageProps) {
  const imgRef = useRef<HTMLImageElement>(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [hasError, setHasError] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          setIsLoaded(true);
          observer.disconnect();
        }
      },
      { rootMargin: '0px 0px 50px 0px' }
    );

    if (imgRef.current) {
      observer.observe(imgRef.current);
    }

    return () => {
      if (imgRef.current) {
        observer.unobserve(imgRef.current);
      }
      observer.disconnect();
    };
  }, []);

  const handleImageError = () => {
    setHasError(true);
  };

  const currentSrc = hasError ? errorPlaceholder || placeholder : (isLoaded ? src : placeholder);

  return (
    <img
      ref={imgRef}
      src={currentSrc}
      alt={alt}
      onError={handleImageError}
      style={{
        display: 'block',
        width: '100%',
        height: '100%',
        objectFit: 'cover',
        opacity: (isLoaded && !hasError) ? 1 : 0.5,
        transition: 'opacity 0.3s ease-in-out'
      }}
    />
  );
}

interface ImageGalleryProps {
  images: { id: number; src: string; placeholder: string; alt: string; error?: boolean }[];
}

function ImageGallery({ images }: ImageGalleryProps) {
  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '20px' }}>
      <h2>Image Gallery</h2>
      {images.map(image => (
        <div key={image.id} style={{ height: '250px', backgroundColor: '#f0f0f0', display: 'flex', alignItems: 'center', justifyContent: 'center', overflow: 'hidden' }}>
          <LazyImage
            src={image.src}
            placeholder={image.placeholder}
            alt={image.alt}
            errorPlaceholder={image.error ? 'https://via.placeholder.com/200/FF0000/FFFFFF?text=Error' : undefined}
          />
        </div>
      ))}
    </div>
  );
}

const dummyImages = Array.from({ length: 20 }, (_, i) => ({
  id: i,
  src: `https://picsum.photos/id/${10 + i}/400/300`,
  placeholder: `https://picsum.photos/id/${10 + i}/20/15`,
  alt: `Random image ${i + 1}`,
