```javascript
// service-worker.js
const CACHE_NAME = 'dynamic-data-cache-v1';

self.addEventListener('fetch', event => {
  // Only apply this strategy to non-HTML requests (e.g., API calls, images, fonts)
  // HTML requests often benefit more from cache-first or network-only
  if (event.request.mode === 'navigate') { // This checks if it's a navigation request (for HTML pages)
    return; // Let the browser handle it normally or apply a different strategy
  }

  event.respondWith(
    fetch(event.request)
      .then(networkResponse => {
        // If network request succeeds, clone the response to store in cache
        // A response can only be consumed once, so clone if you want to also return it.
        const responseToCache = networkResponse.clone();
        caches.open(CACHE_NAME)
          .then(cache => {
            cache.put(event.request, responseToCache);
          });
        return networkResponse;
      })
      .catch(() => {
        // Network failed, try to get from cache
        return caches.match(event.request);
      })
  );
});

self.addEventListener('activate', event => {
  event.waitUntil(
    caches.keys().then(cacheNames => {
      return Promise.all(
        cacheNames.map(cacheName => {
          if (cacheName !== CACHE_NAME) {
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
});
```

