// server.js
const express = require('express');
const path = require('path');
const fs = require('fs');
const crypto = require('crypto');
const app = express();
const PORT = 3000;

const publicPath = path.join(__dirname, 'public');

// Middleware for ETag generation (for index.html)
app.use((req, res, next) => {
  if (req.path === '/index.html' || req.path === '/') {
    const filePath = path.join(publicPath, 'index.html');
    fs.readFile(filePath, (err, data) => {
      if (err) {
        return next(err);
      }
      const hash = crypto.createHash('md5').update(data).digest('hex');
      res.setHeader('ETag', `"${hash}"`);
      res.setHeader('Last-Modified', new Date().toUTCString()); // Use current time for simplicity
      next();
    });
  } else {
    next();
  }
});

// 1. Serve index.html (or root path) with no-cache strategy
app.get('/', (req, res) => {
  res.setHeader('Cache-Control', 'no-cache, must-revalidate'); // Always revalidate
  // Check for conditional request headers if ETag/Last-Modified are used
  if (req.fresh) { // Express's req.fresh checks If-None-Match and If-Modified-Since
    return res.sendStatus(304); // Send Not Modified if browser's cache is still valid
  }
  res.sendFile(path.join(publicPath, 'index.html'));
});

// 2. Serve versioned assets (app.js, style.css) with aggressive caching
app.get('/app.js', (req, res) => {
  res.setHeader('Cache-Control', 'public, max-age=31536000, immutable'); // Cache for 1 year, never revalidate
  res.sendFile(path.join(publicPath, 'app.js'));
});

app.get('/style.css', (req, res) => {
  res.setHeader('Cache-Control', 'public, max-age=31536000, immutable'); // Cache for 1 year, never revalidate
  res.sendFile(path.join(publicPath, 'style.css'));
});

// 4. Bonus: Dynamic API endpoint with no-store
app.get('/api/data', (req, res) => {
  res.setHeader('Cache-Control', 'no-store'); // Never cache this response
  res.json({ timestamp: new Date().toISOString(), message: 'Sensitive dynamic data' });
});

// Serve other static files (optional, for robustness)
app.use(express.static(publicPath));

// Start the server
app.listen(PORT, () => {
  console.log(`Server running on http://localhost:${PORT}`);
  console.log('Open http://localhost:3000 in your browser and check the Network tab.');
  console.log('Reload the page multiple times to observe caching behavior.');
});

// public/index.html (Note: scripts and links paths assume they are served directly at root or with path segments)
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Caching Demo</title>
    <link rel="stylesheet" href="/style.css">
</head>
<body>
    <h1>HTTP Caching Demo</h1>
    <p>Check network tab for cache headers!</p>
    <script src="/app.js"></script>
    <button id="fetchApi">Fetch API Data</button>
    <div id="api-data"></div>
    <script>
        document.getElementById('fetchApi').addEventListener('click', async () => {
            const response = await fetch('/api/data');
            const data = await response.json();
            document.getElementById('api-data').innerText = JSON.stringify(data, null, 2);
        });
    </script>
</body>
</html>

