// public/service-worker.js
const CACHE_NAME = 'offline-fallback-cache-v1';
const OFFLINE_URL = '/offline.html';
const ASSETS_TO_CACHE = [
  '/', // '/' represents index.html
  '/index.html',
  '/style.css',
  OFFLINE_URL
];

self.addEventListener('install', event => {
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then(cache => {
        console.log('[Service Worker] Caching critical assets for offline use.');
        return cache.addAll(ASSETS_TO_CACHE);
      })
      .catch(error => console.error('[Service Worker] Failed to cache assets during install:', error))
  );
});

self.addEventListener('activate', event => {
  event.waitUntil(
    // Clean up old caches if any
    caches.keys().then(cacheNames => {
      return Promise.all(
        cacheNames.map(cacheName => {
          if (cacheName !== CACHE_NAME) {
            console.log('[Service Worker] Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
  // Take control of uncontrolled clients immediately
  self.clients.claim();
  console.log('[Service Worker] Activated and claimed clients.');
});

self.addEventListener('fetch', event => {
  // Handle navigation requests (e.g., direct page loads) and other requests
  event.respondWith(
    fetch(event.request)
      .then(networkResponse => {
        // If network is successful, cache the new response and return it
        // Make sure the response is valid before caching
        if (!networkResponse || networkResponse.status !== 200 || networkResponse.type !== 'basic') {
          return networkResponse; // Don't cache opaque responses or errors
        }
        const responseToCache = networkResponse.clone();
        caches.open(CACHE_NAME)
          .then(cache => {
            cache.put(event.request, responseToCache);
          });
        return networkResponse;
      })
      .catch(() => {
        // Network failed (e.g., offline or network error)
        // Try to serve from cache first
        return caches.match(event.request)
          .then(cachedResponse => {
            if (cachedResponse) {
              console.log('[Service Worker] Serving from cache:', event.request.url);
              return cachedResponse;
            }
            // If not in cache, fallback to the offline page for navigation requests
            if (event.request.mode === 'navigate') {
                console.log('[Service Worker] Network failed and not in cache, serving offline page for navigation.');
                return caches.match(OFFLINE_URL);
            }
            // For other asset requests (images, scripts, etc.), return a generic error or nothing
            return new Response('Network error or not found in cache.', { status: 503, statusText: 'Service Unavailable' });
          });
      })
  );
});

