<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Fibonacci Worker</title>
    <style>
        body { font-family: sans-serif; text-align: center; padding: 20px; }
        input, button { margin: 10px; padding: 8px; }
        #counter { font-size: 2em; margin-top: 20px; color: green; }
        #result { font-size: 1.5em; margin-top: 20px; color: blue; }
    </style>
</head>
<body>
    <h1>Fibonacci Calculator (Web Worker Demo)</h1>
    <div>
        <label for="fib-input">Calculate Fibonacci for N:</label>
        <input type="number" id="fib-input" value="40">
        <button id="calculate-btn">Calculate</button>
    </div>
    <p id="result">Result: </p>
    <p id="counter">Counter: 0</p>

    <script>
        const fibInput = document.getElementById('fib-input');
        const calculateBtn = document.getElementById('calculate-btn');
        const resultDiv = document.getElementById('result');
        const counterDiv = document.getElementById('counter');
        let counter = 0;

        // Simple counter to prove UI responsiveness
        setInterval(() => {
            counter++;
            counterDiv.textContent = `Counter: ${counter}`;
        }, 100);

        // Web Worker integration
        if (window.Worker) {
            const fibWorker = new Worker('/worker.js'); // Path to your worker script

            fibWorker.onmessage = function(event) {
                const { result, n } = event.data;
                resultDiv.textContent = `Result for fib(${n}): ${result}`;
                console.log(`Calculation for fib(${n}) completed in worker.`);
            };

            fibWorker.onerror = function(error) {
                console.error('Worker error:', error);
                resultDiv.textContent = 'Result: Error during calculation.';
            };

            calculateBtn.addEventListener('click', () => {
                const n = parseInt(fibInput.value, 10);
                if (isNaN(n) || n < 0) {
                    alert('Please enter a valid positive number for N.');
                    return;
                }
                resultDiv.textContent = 'Result: Calculating... (Check Counter for responsiveness)';
                console.log(`Sending fib(${n}) to worker...`);
                fibWorker.postMessage({ n: n }); // Send data to the worker
            });
        } else {
            resultDiv.textContent = 'Web Workers are not supported in this browser.';
            calculateBtn.disabled = true;
        }
    </script>
</body>
</html>

// public/worker.js
// Fibonacci calculation function
function fibonacci(num) {
    if (num <= 1) return num;
    let a = 0, b = 1, temp;
    for (let i = 2; i <= num; i++) {
        temp = a + b;
        a = b;
        b = temp;
    }
    return b;
    // Recursive (more demonstrative of blocking if not in worker, but less efficient for large N)
    // if (num <= 1) return num;
    // return fibonacci(num - 1) + fibonacci(num - 2);
}

// Listen for messages from the main thread
onmessage = function(event) {
    const n = event.data.n;
    console.log(`Worker: Calculating fib(${n})...`);
    const result = fibonacci(n);
    console.log(`Worker: Finished fib(${n}) = ${result}.`);
    // Send the result back to the main thread
    postMessage({ result: result, n: n });
};

