// public/service-worker.js
const CACHE_NAME_STATIC = 'static-assets-cache-v1';
const CACHE_NAME_DYNAMIC = 'dynamic-data-cache-v1';
const CACHE_NAME_IMAGES = 'images-cache-v1';
const OFFLINE_URL = '/offline.html';

const staticAssetsToCache = [
  '/',
  '/index.html',
  '/style.css',
  OFFLINE_URL
];

self.addEventListener('install', event => {
  event.waitUntil(
    Promise.all([
      caches.open(CACHE_NAME_STATIC).then(cache => cache.addAll(staticAssetsToCache)),
      // Pre-cache common images if desired, otherwise let stale-while-revalidate handle it
      // caches.open(CACHE_NAME_IMAGES).then(cache => cache.addAll(['/sample.png']))
    ]).then(() => {
      console.log('[Service Worker] Installation successful. Static assets cached.');
    }).catch(error => {
      console.error('[Service Worker] Installation failed:', error);
    })
  );
});

self.addEventListener('activate', event => {
  event.waitUntil(
    caches.keys().then(cacheNames => {
      return Promise.all(
        cacheNames.map(cacheName => {
          if (![CACHE_NAME_STATIC, CACHE_NAME_DYNAMIC, CACHE_NAME_IMAGES].includes(cacheName)) {
            console.log('[Service Worker] Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
  self.clients.claim(); // Take control of uncontrolled clients
  console.log('[Service Worker] Activated and claimed clients.');
});

self.addEventListener('fetch', event => {
  const requestUrl = new URL(event.request.url);
  const requestPath = requestUrl.pathname;
  const isNavigation = event.request.mode === 'navigate';

  // 1. Cache-First for Static HTML/CSS (and navigation requests fallback)
  if (isNavigation || staticAssetsToCache.includes(requestPath)) {
    event.respondWith(
      caches.match(event.request).then(cachedResponse => {
        if (cachedResponse) {
          console.log(`[Cache-First] Serving ${requestPath} from cache.`);
          return cachedResponse;
        }
        console.log(`[Cache-First] Fetching ${requestPath} from network.`);
        return fetch(event.request).then(networkResponse => {
          return caches.open(CACHE_NAME_STATIC).then(cache => {
            cache.put(event.request, networkResponse.clone());
            return networkResponse;
          });
        }).catch(() => {
          // Fallback for navigation requests if network and cache fail
          if (isNavigation) {
            console.log('[Fallback] Network and cache failed for navigation, serving offline page.');
            return caches.match(OFFLINE_URL);
          }
          return new Response('Offline and not in cache.', { status: 503 });
        });
      })
    );
    return;
  }

  // 2. Network-First for API Requests
  if (requestPath.startsWith('/api/data')) {
    event.respondWith(
      fetch(event.request).then(networkResponse => {
        console.log(`[Network-First] Fetching API ${requestPath} from network.`);
        return caches.open(CACHE_NAME_DYNAMIC).then(cache => {
          cache.put(event.request, networkResponse.clone());
          return networkResponse;
        });
      }).catch(() => {
        console.log(`[Network-First] Network failed for API ${requestPath}, serving from cache.`);
        return caches.match(event.request);
      })
    );
    return;
  }

  // 3. Stale-While-Revalidate for Images
  if (requestPath.match(/\.(png|jpg|jpeg|gif|webp)$/i)) {
    event.respondWith(
      caches.match(event.request).then(cachedResponse => {
        const fetchPromise = fetch(event.request).then(networkResponse => {
          console.log(`[Stale-While-Revalidate] Fetching image ${requestPath} from network.`);
          return caches.open(CACHE_NAME_IMAGES).then(cache => {
            cache.put(event.request, networkResponse.clone());
            return networkResponse;
          });
        }).catch(error => {
            console.error(`[Stale-While-Revalidate] Network revalidation failed for image ${requestPath}:`, error);
            return new Response('Image network error.', { status: 503 });
        });

        if (cachedResponse) {
          console.log(`[Stale-While-Revalidate] Serving image ${requestPath} from cache.`);
          return cachedResponse; // Return cached response immediately
        }
        return fetchPromise; // If no cached, wait for network
      })
    );
    return;
  }

  // Default: Go to network for anything else
  event.respondWith(fetch(event.request));
});

