// public/index.html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Offline Notes</title>
    <style>
        body { font-family: sans-serif; padding: 20px; max-width: 800px; margin: auto; }
        #note-form { border: 1px solid #ccc; padding: 20px; margin-bottom: 20px; }
        input[type="text"], textarea { width: 100%; padding: 8px; margin-bottom: 10px; border: 1px solid #ddd; }
        button { padding: 10px 15px; background-color: #007bff; color: white; border: none; cursor: pointer; }
        .note-item { border: 1px solid #eee; padding: 15px; margin-bottom: 10px; background-color: #f9f9f9; display: flex; justify-content: space-between; align-items: flex-start; }
        .note-item .content { flex-grow: 1; }
        .note-item h3 { margin-top: 0; color: #333; }
        .note-item p { font-size: 0.9em; color: #666; }
        .note-item button.delete-btn { background-color: #dc3545; margin-left: 10px; border-radius: 4px; }
    </style>
</head>
<body>
    <h1>Offline Note-Taking App</h1>

    <div id="note-form">
        <h2>Add New Note</h2>
        <input type="text" id="note-title" placeholder="Note Title" required>
        <textarea id="note-content" placeholder="Note Content" rows="5" required></textarea>
        <button id="save-note-btn">Save Note</button>
    </div>

    <div id="notes-list">
        <h2>Your Notes</h2>
        <!-- Notes will be loaded here -->
        <p id="no-notes-message">No notes yet. Start by adding one!</p>
    </div>

    <script>
        const DB_NAME = 'OfflineNotesDB';
        const DB_VERSION = 1;
        const STORE_NAME = 'notes';

        let db; // Global variable for the IndexedDB database instance

        const noteTitleInput = document.getElementById('note-title');
        const noteContentInput = document.getElementById('note-content');
        const saveNoteBtn = document.getElementById('save-note-btn');
        const notesListDiv = document.getElementById('notes-list');
        const noNotesMessage = document.getElementById('no-notes-message');

        // 2. IndexedDB Setup: Open database and create object store
        function openDatabase() {
            return new Promise((resolve, reject) => {
                const request = indexedDB.open(DB_NAME, DB_VERSION);

                request.onerror = event => {
                    console.error('IndexedDB error:', event.target.errorCode);
                    reject('Error opening database');
                };

                request.onsuccess = event => {
                    db = event.target.result;
                    console.log('Database opened successfully');
                    resolve(db);
                };

                request.onupgradeneeded = event => {
                    db = event.target.result;
                    if (!db.objectStoreNames.contains(STORE_NAME)) {
                        const objectStore = db.createObjectStore(STORE_NAME, { keyPath: 'id', autoIncrement: true });
                        // Optionally create indexes if you need to query by other fields
                        objectStore.createIndex('timestamp', 'timestamp', { unique: false });
                        console.log('Object store created/upgraded');
                    }
                };
            });
        }

        // 3. Add Note operation
        async function addNote(title, content) {
            if (!db) await openDatabase();

            const transaction = db.transaction([STORE_NAME], 'readwrite');
            const objectStore = transaction.objectStore(STORE_NAME);
            const newNote = {
                title: title,
                content: content,
                timestamp: new Date().toISOString()
            };

            const request = objectStore.add(newNote);

            request.onsuccess = () => {
                console.log('Note added successfully:', newNote);
                displayNotes(); // Refresh list after adding
                noteTitleInput.value = '';
                noteContentInput.value = '';
            };

            request.onerror = event => {
                console.error('Error adding note:', event.target.error);
                alert('Error saving note.');
            };
        }

        // 3. Display Notes operation
        async function displayNotes() {
            if (!db) await openDatabase();

            // Clear existing notes from display
            const existingNoteItems = notesListDiv.querySelectorAll('.note-item');
            existingNoteItems.forEach(item => item.remove());

            const transaction = db.transaction([STORE_NAME], 'readonly');
            const objectStore = transaction.objectStore(STORE_NAME);
            const request = objectStore.getAll(); // Get all notes

            request.onsuccess = event => {
                const notes = event.target.result;
                if (notes.length === 0) {
                    noNotesMessage.style.display = 'block';
                } else {
                    noNotesMessage.style.display = 'none';
                    // Sort notes by timestamp in descending order (latest first)
                    notes.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

                    notes.forEach(note => {
                        const noteElement = document.createElement('div');
                        noteElement.className = 'note-item';
                        noteElement.dataset.id = note.id; // Store ID for delete operation
                        noteElement.innerHTML = `
                            <div class="content">
                                <h3>${note.title}</h3>
                                <p>${note.content}</p>
                                <p><em>Saved: ${new Date(note.timestamp).toLocaleString()}</em></p>
                            </div>
                            <button class="delete-btn">Delete</button>
                        `;
                        notesListDiv.appendChild(noteElement);

                        // Optional: Add delete button listener
                        noteElement.querySelector('.delete-btn').addEventListener('click', () => deleteNote(note.id));
                    });
                }
            };

            request.onerror = event => {
                console.error('Error fetching notes:', event.target.error);
                alert('Error loading notes.');
            };
        }

        // Optional: Delete Note operation
        async function deleteNote(id) {
            if (!db) await openDatabase();

            const transaction = db.transaction([STORE_NAME], 'readwrite');
            const objectStore = transaction.objectStore(STORE_NAME);
            const request = objectStore.delete(id);

            request.onsuccess = () => {
                console.log('Note deleted:', id);
                displayNotes(); // Refresh list after deleting
            };

            request.onerror = event => {
                console.error('Error deleting note:', event.target.error);
                alert('Error deleting note.');
            };
        }

        // Event listener for saving note
        saveNoteBtn.addEventListener('click', () => {
            const title = noteTitleInput.value.trim();
            const content = noteContentInput.value.trim();
            if (title && content) {
                addNote(title, content);
            } else {
                alert('Please enter both title and content.');
            }
        });

        // Initialize database and display notes on page load
        window.addEventListener('DOMContentLoaded', async () => {
            try {
                await openDatabase();
                displayNotes();
            } catch (error) {
                console.error('Initialization failed:', error);
                alert('Failed to initialize note app. IndexedDB might be unavailable.');
            }
        });
    </script>
</body>
</html>

