function withLoading(WrappedComponent) {
  function WithLoading(props) { // This is the new component returned by the HOC
    const [isLoading, setIsLoading] = useState(false);

    const startLoading = () => setIsLoading(true);
    const stopLoading = () => setIsLoading(false);

    // Render the WrappedComponent, passing new props (isLoading, startLoading, stopLoading)
    // along with any original props (spread via {...props})
    return (
      <WrappedComponent
        isLoading={isLoading}
        startLoading={startLoading}
        stopLoading={stopLoading}
        {...props}
      />
    );
  }

  // For easier debugging in React DevTools
  WithLoading.displayName = `withLoading(${getDisplayName(WrappedComponent)})`;
  return WithLoading;
}

// Helper to get component's display name
function getDisplayName(WrappedComponent) {
  return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

