// sw.js (Service Worker file):
const CACHE_STATIC_NAME = 'static-v1';
const CACHE_API_NAME = 'api-v1';
const STATIC_ASSETS = [
  '/',
  'index.html',
  'style.css',
  'app.js',
  'image.png' 
];

self.addEventListener('install', event => {
  console.log('[Service Worker] Installing Service Worker ...', event);
  event.waitUntil(
    caches.open(CACHE_STATIC_NAME)
      .then(cache => {
        console.log('[Service Worker] Pre-caching App Shell');
        return cache.addAll(STATIC_ASSETS);
      })
  );
});

self.addEventListener('activate', event => {
  console.log('[Service Worker] Activating Service Worker ....', event);
  event.waitUntil(
    caches.keys().then(keyList => {
      return Promise.all(keyList.map(key => {
        if (key !== CACHE_STATIC_NAME && key !== CACHE_API_NAME) {
          console.log('[Service Worker] Removing old cache:', key);
          return caches.delete(key);
        }
      }))
    })
  );
  return self.clients.claim();
});

self.addEventListener('fetch', event => {
  const url = new URL(event.request.url);

  if (STATIC_ASSETS.includes(url.pathname) || url.origin === location.origin) {
    // Cache-First for static assets (and same-origin navigation/assets)
    event.respondWith(
      caches.match(event.request)
        .then(cachedResponse => {
          if (cachedResponse) {
            // Update status message on client if served from cache
            event.request.mode === 'navigate' && self.clients.matchAll().then(clients => {
                clients.forEach(client => client.postMessage('offline'));
            });
            return cachedResponse;
          }
          return fetch(event.request)
            .then(response => {
              if (!response || response.status !== 200 || response.type !== 'basic') {
                return response;
              }
              const responseToCache = response.clone();
              caches.open(CACHE_STATIC_NAME)
                .then(cache => {
                  cache.put(event.request, responseToCache);
                });
              return response;
            })
            .catch(err => {
              // Fallback for network issues even for static assets
              // For real apps, you'd serve an offline page here
              console.log('[Service Worker] Fetch failed for static asset, trying cache:', err);
              return caches.match(event.request);
            });
        })
    );
  } else if (url.pathname.startsWith('/api/data')) {
    // Network-First, then Cache for API calls
    event.respondWith(
      fetch(event.request)
        .then(response => {
          // If fetch successful, cache it and return
          if (!response || response.status !== 200 || response.type !== 'basic') {
            return response;
          }
          const responseToCache = response.clone();
          caches.open(CACHE_API_NAME)
            .then(cache => {
              cache.put(event.request, responseToCache);
            });
          return response;
        })
        .catch(err => {
          // If network fails, try to return from cache
          console.log('[Service Worker] API Fetch failed, falling back to cache:', err);
          self.clients.matchAll().then(clients => {
              clients.forEach(client => client.postMessage('offline'));
          });
          return caches.match(event.request);
        })
    );
  } else {
    // Default: network-only for other requests (e.g., third-party)
    return;
  }
});

