// server.js
const express = require('express');
const app = express();
const PORT = 8080;

// Middleware to parse JSON body
app.use(express.json());

// Custom CORS Middleware
app.use((req, res, next) => {
  // Set the allowed origin
  res.header('Access-Control-Allow-Origin', 'http://localhost:3000');

  // Set allowed HTTP methods
  res.header('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');

  // Set allowed request headers (includes Content-Type and Authorization)
  res.header('Access-Control-Allow-Headers', 'Content-Type, Authorization');

  // Handle preflight requests
  if (req.method === 'OPTIONS') {
    // For preflight, immediately respond with 200 OK
    return res.sendStatus(200);
  }

  // Continue to the next middleware/route handler for actual requests
  next();
});

// Example GET route
app.get('/data', (req, res) => {
  res.json({ message: 'Data from server!', timestamp: new Date().toISOString() });
});

// Example POST route
app.post('/submit', (req, res) => {
  console.log('Received data:', req.body);
  res.status(200).json({ message: 'Data received successfully!', yourData: req.body });
});

app.listen(PORT, () => {
  console.log(`Server running on http://localhost:${PORT}`);
});

