import React, { useEffect, useState, useRef } from 'react';

// Helper to get component's display name for HOC
function getDisplayName(WrappedComponent) {
  return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

// Define the withLogger HOC
function withLogger(WrappedComponent) {
  const WithLoggerComponent = (props) => {
    const initialRender = useRef(true);
    const prevProps = useRef({});

    // Log on Mount
    useEffect(() => {
      console.log(`[HOC Logger] ${getDisplayName(WrappedComponent)} mounted.`);
      initialRender.current = false;

      // Log on Unmount
      return () => {
        console.log(`[HOC Logger] ${getDisplayName(WrappedComponent)} unmounted.`);
      };
    }, []); // Empty dependency array means this runs once on mount and cleanup on unmount

    // Log on Update (only if props change significantly)
    useEffect(() => {
      if (!initialRender.current) { // Prevent logging on initial mount as an update
        const changedProps = Object.keys(props).filter(
          (key) => props[key] !== prevProps.current[key]
        );
        if (changedProps.length > 0) {
          console.log(
            `[HOC Logger] ${getDisplayName(WrappedComponent)} updated. Changed props:`, 
            changedProps.reduce((acc, key) => ({ ...acc, [key]: props[key] }), {})
          );
        } else {
          console.log(`[HOC Logger] ${getDisplayName(WrappedComponent)} updated (no prop changes).`);
        }
      }
      prevProps.current = props;
    }); // No dependency array: runs on every render after initial

    return <WrappedComponent {...props} />;
  };

  WithLoggerComponent.displayName = `withLogger(${getDisplayName(WrappedComponent)})`;
  return WithLoggerComponent;
}

// Example Wrapped Component
function Counter({ initialValue, incrementBy }) {
  const [count, setCount] = useState(initialValue);

  const handleClick = () => {
    setCount(prevCount => prevCount + incrementBy);
  };

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={handleClick}>Increment</button>
      <p>Increment By: {incrementBy}</p>
    </div>
  );
}

// Apply the HOC to the Counter component
const EnhancedCounter = withLogger(Counter);
export default EnhancedCounter;

