import React, { useState } from 'react';

// Helper to get component's display name for HOC
function getDisplayName(WrappedComponent) {
  return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

// Implement the withFormValidation HOC
function withFormValidation(WrappedComponent) {
  const WithFormValidationComponent = (props) => {
    const [errors, setErrors] = useState({});

    const validateField = (fieldName, value) => {
      let error = '';
      switch (fieldName) {
        case 'username':
          if (!value) {
            error = 'Username is required.';
          }
          break;
        case 'email':
          if (!value) {
            error = 'Email is required.';
          } else if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(value)) {
            error = 'Invalid email format.';
          }
          break;
        default:
          break;
      }
      setErrors(prevErrors => ({
        ...prevErrors,
        [fieldName]: error,
      }));
      return !error; // Return true if no error, false otherwise
    };

    const validateForm = (formDataToCheck) => {
      const newErrors = {};
      let isValid = true;

      // For simplicity, validate all fields that are expected to be in the form
      // In a real app, formDataToCheck would be passed from the wrapped component.
      // Here, we assume a simple structure or access values from context/props if needed.
      // For this example, we'll re-validate based on a dummy structure or expected fields.

      // Re-validate based on fields we know the form has
      if (!formDataToCheck.username) {
        newErrors.username = 'Username is required.';
        isValid = false;
      }
      if (!formDataToCheck.email) {
        newErrors.email = 'Email is required.';
        isValid = false;
      } else if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(formDataToCheck.email)) {
        newErrors.email = 'Invalid email format.';
        isValid = false;
      }

      setErrors(newErrors);
      return isValid;
    };

    // Clear all errors
    const clearErrors = () => {
      setErrors({});
    };

    return (
      <WrappedComponent
        {...props}
        errors={errors}
        validateField={validateField}
        validateForm={(data) => validateForm(data || props.formData)} // Pass data from wrapped component's state if available
        clearErrors={clearErrors}
      />
    );
  };

  WithFormValidationComponent.displayName = `withFormValidation(${getDisplayName(WrappedComponent)})`;
  return WithFormValidationComponent;
}

// ContactForm Component (to be wrapped by HOC)
function ContactForm({ errors, validateField, validateForm, clearErrors }) {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    // Optional: Validate on change too, or just on blur
    // validateField(name, value);
  };

  const handleBlur = (e) => {
    const { name, value } = e.target;
    validateField(name, value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    clearErrors(); // Clear errors before re-validating whole form
    const isValid = validateForm(formData); // Pass current form data to HOC for full validation
    if (isValid) {
      console.log('Form is valid! Submitting:', formData);
      alert('Form submitted successfully!');
      // In a real app, you'd send data to server here
    } else {
      console.log('Form has errors:', errors);
      alert('Please fix the errors in the form.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          name="username"
          value={formData.username}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {errors.username && <p style={{ color: 'red' }}>{errors.username}</p>}
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {errors.email && <p style={{ color: 'red' }}>{errors.email}</p>}
      </div>
      <button type="submit">Submit</button>
    </form>
  );
}

// Apply the HOC and export
const EnhancedContactForm = withFormValidation(ContactForm);
export default EnhancedContactForm;

