  type: 'SET_PRODUCT_FILTER',
  payload: filter,
});
```

**Component Signature:**
```typescript
interface ProductListProps {
  filteredProducts: Array<{ id: number; name: string; category: string; price: number; }>;
  setProductFilter: (filter: string) => void;
}

// function ProductList(props: ProductListProps) { /* ... */ }
```

Provide only the `mapStateToProps` and `mapDispatchToProps` implementations.

questions.answer:
```typescript
import { createSelector } from 'reselect'; // Recommended for derived state
import { setFilter } from './actions';

// Selector to get products list and filter string from state
const getProductsList = (state) => state.products.list;
const getProductFilter = (state) => state.products.filter;

// Memoized selector for filtered products
const getFilteredProducts = createSelector(
  [getProductsList, getProductFilter],
  (products, filter) => {
    if (!filter) {
      return products;
    }
    return products.filter(product =>
      product.name.toLowerCase().includes(filter.toLowerCase()) ||
      product.category.toLowerCase().includes(filter.toLowerCase())
    );
  }
);

const mapStateToProps = (state) => ({
  filteredProducts: getFilteredProducts(state),
});

const mapDispatchToProps = {
  setProductFilter: setFilter,
};

