import React, { useState, useEffect, ComponentType } from 'react';

// HOC: withLoading
// Takes a React component and returns a new component that handles loading state
const withLoading = <P extends { isLoading?: boolean }>(WrappedComponent: ComponentType<P>): React.FC<P> => {
  const WithLoadingComponent: React.FC<P> = (props) => {
    const { isLoading, ...otherProps } = props;

    if (isLoading) {
      return <div>Loading...</div>;
    }

    // Cast 'otherProps' to the type of the WrappedComponent's props minus isLoading
    // This ensures type safety for the props passed to WrappedComponent
    return <WrappedComponent {...(otherProps as P)} />;
  };

  WithLoadingComponent.displayName = `withLoading(${getDisplayName(WrappedComponent)})`;
  return WithLoadingComponent;
};

// Helper to get display name for HOC
function getDisplayName<P>(WrappedComponent: ComponentType<P>) {
  return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

interface User {
  id: number;
  name: string;
}

interface UserListProps {
  users: User[];
  isLoading?: boolean; // This prop is consumed by the HOC, not directly by UserList
}

const UserList: React.FC<UserListProps> = ({ users }) => {
  console.log('UserList rendered'); // Will only render when not loading
  return (
    <div>
      <h2>Users</h2>
      <ul>
        {users.map(user => (
          <li key={user.id}>{user.name}</li>
        ))}
      </ul>
    </div>
  );
};

// Main App Component
export default function App() {
  const [users, setUsers] = useState<User[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));
      setUsers([
        { id: 1, name: 'Alice' },
        { id: 2, name: 'Bob' },
        { id: 3, name: 'Charlie' }
      ]);
      setIsLoading(false);
    };

    fetchData();
  }, []);

  // Use the withLoading HOC to enhance UserList
  const UserListWithLoading = withLoading(UserList);

  // Pass the necessary props including isLoading to the HOC-enhanced component
  return <UserListWithLoading users={users} isLoading={isLoading} />;
}

