function Machine() {
  let enabled = false; // Private variable via closure

  this.enable = function() {
    enabled = true;
  };

  this.disable = function() {
    enabled = false;
  };

  this.isEnabled = function() {
    return enabled;
  };
}

function CoffeeMachine(power) {
  // Inherit methods from Machine by calling its constructor
  // This copies public methods/properties defined on 'this' from Machine to CoffeeMachine instance
  Machine.call(this);

  let waterAmount = 0; // Private variable via closure specific to CoffeeMachine

  this.setWaterAmount = function(amount) {
    if (amount < 0) {
        console.error("Water amount cannot be negative");
        return;
    }
    waterAmount = amount;
  };

  this.getWaterAmount = function() {
    return waterAmount;
  };

  this.brew = function() {
    if (this.isEnabled()) {
      console.log(`Making coffee with ${waterAmount}ml of water using ${power}W power`);
    } else {
      console.log("Coffee machine is off");
    }
  };
}

const coffeeMachine = new CoffeeMachine(1000);
console.log("Is enabled initially?", coffeeMachine.isEnabled()); // false
coffeeMachine.enable();
console.log("Is enabled after enable()?", coffeeMachine.isEnabled()); // true
coffeeMachine.setWaterAmount(200);
console.log("Current water amount:", coffeeMachine.getWaterAmount()); // 200
coffeeMachine.brew(); // "Making coffee with 200ml of water using 1000W power"
coffeeMachine.disable();
coffeeMachine.brew(); // "Coffee machine is off"

// Try to access private variables (will fail or return undefined)
console.log(coffeeMachine.enabled);    // undefined
console.log(coffeeMachine.waterAmount); // undefined

