tasks.solutionCode:
const LOG_LEVELS = {
  'DEBUG': 0,
  'INFO': 1,
  'WARN': 2,
  'ERROR': 3
};

function createLogger(initialLevel = 'INFO') {
  let _logs = [];
  let currentLevel = LOG_LEVELS[initialLevel.toUpperCase()] || LOG_LEVELS['INFO'];

  return {
    log: function(message, level = 'INFO') {
      const messageLevel = LOG_LEVELS[level.toUpperCase()];
      if (messageLevel !== undefined && messageLevel >= currentLevel) {
        _logs.push(`[${level.toUpperCase()}] ${message}`);
      }
    },
    setLogLevel: function(newLevel) {
      const levelValue = LOG_LEVELS[newLevel.toUpperCase()];
      if (levelValue !== undefined) {
        currentLevel = levelValue;
      } else {
        console.warn(`Invalid log level: ${newLevel}`);
      }
    },
    getLogs: function() {
      return [..._logs]; // Return a copy to prevent external modification of private array
    },
    clearLogs: function() {
      _logs = [];
    }
  };
}

function createTimedLogger(initialLevel = 'INFO') {
  const logger = createLogger(initialLevel);

  // Override the log method
  const originalLog = logger.log;
  logger.log = function(message, level = 'INFO') {
    const timestamp = new Date().toISOString();
    originalLog.call(this, `(${timestamp}) ${message}`, level);
  };

  return logger;
}

