function UserAccount() {
  let balance = 0;
  let transactionHistory = [];

  const formatTransaction = (type, amount, success) => {
    if (success) {
      return `${type}: ${type === 'Deposit' ? '+' : '-'}$${amount.toFixed(2)}`;
    } else {
      return `Withdrawal failed: Insufficient funds. Attempted: $${amount.toFixed(2)}`;
    }
  };

  return {
    deposit: function(amount) {
      if (amount <= 0) {
        console.log('Deposit amount must be positive.');
        return;
      }
      balance += amount;
      transactionHistory.push(formatTransaction('Deposit', amount, true));
      console.log(`Deposited $${amount.toFixed(2)}. New balance: $${balance.toFixed(2)}`);
    },
    withdraw: function(amount) {
      if (amount <= 0) {
        console.log('Withdrawal amount must be positive.');
        return;
      }
      if (amount <= balance) {
        balance -= amount;
        transactionHistory.push(formatTransaction('Withdraw', amount, true));
        console.log(`Withdrew $${amount.toFixed(2)}. New balance: $${balance.toFixed(2)}`);
      } else {
        transactionHistory.push(formatTransaction('Withdraw', amount, false));
        console.log('Insufficient funds. Withdrawal failed.');
      }
    },
    getStatement: function() {
      // Return a copy to prevent external modification of the private array
      return [...transactionHistory]; 
    },
    // For testing purposes, though balance is conceptually private
    _getCurrentBalance: function() {
      return balance;
    }
  };
}

