const _startTime = Symbol('startTime');
const _running = Symbol('running');
const _totalTime = Symbol('totalTime');

class Stopwatch {
  constructor() {
    this[_startTime] = null;
    this[_running] = false;
    this[_totalTime] = 0; // Accumulates total elapsed time
  }

  start() {
    if (this[_running]) {
      console.log('Stopwatch is already running.');
      return;
    }
    this[_startTime] = Date.now();
    this[_running] = true;
    console.log('Stopwatch started.');
  }

  stop() {
    if (!this[_running]) {
      console.log('Stopwatch is not running.');
      return;
    }
    this[_totalTime] += Date.now() - this[_startTime];
    this[_running] = false;
    this[_startTime] = null; // Clear start time after stopping
    console.log('Stopwatch stopped.');
  }

  reset() {
    this[_totalTime] = 0;
    this[_startTime] = null;
    this[_running] = false;
    console.log('Stopwatch reset.');
  }

  getElapsedTime() {
    let currentElapsedTime = this[_totalTime];
    if (this[_running]) {
      currentElapsedTime += Date.now() - this[_startTime];
    }
    return currentElapsedTime;
  }
}

