import React from 'react';
import { connect } from 'react-redux';

const INCREMENT = 'INCREMENT';
const DECREMENT = 'DECREMENT';
const RESET = 'RESET';

const increment = (amount = 1) => ({ type: INCREMENT, payload: amount });
const decrement = (amount = 1) => ({ type: DECREMENT, payload: amount });
const reset = () => ({ type: RESET });

class CounterComponent extends React.Component {
  render() {
    const { count, increment, decrement, reset, logAndIncrement, incrementById } = this.props;
    return (
      <div>
        <h1>Counter: {count}</h1>
        <button onClick={() => increment(1)}>Increment</button>
        <button onClick={() => decrement(1)}>Decrement</button>
        <button onClick={reset}>Reset</button>
        <button onClick={() => logAndIncrement(5)}>Log & Increment by 5</button>
        {this.props.initialIncrement && (
          <button onClick={incrementById}>Increment by ownProps.initialIncrement</button>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({ count: state });

// 1. Object Shorthand
const mapDispatchToPropsA = {
  increment,
  decrement
};

// 2. Function with dispatch parameter
const mapDispatchToPropsB = (dispatch) => ({
  reset: () => dispatch(reset()),
  logAndIncrement: (amount) => {
    console.log(`Action: Increment by ${amount}`);
    dispatch(increment(amount));
  }
});

// 3. Function with dispatch and ownProps parameters
const mapDispatchToPropsC = (dispatch, ownProps) => ({
  incrementById: () => {
    if (ownProps.initialIncrement) {
      dispatch(increment(ownProps.initialIncrement));
    }
  }
});

// For testing purposes, you would connect the component with one of these:
// export default connect(mapStateToProps, mapDispatchToPropsA)(CounterComponent); // For increment/decrement
// export default connect(mapStateToProps, mapDispatchToPropsB)(CounterComponent); // For reset/logAndIncrement
// export default connect(mapStateToProps, mapDispatchToPropsC)(CounterComponent); // For incrementById

// Example of combining for a single component: 
// You would typically use just one mapDispatchToProps definition for a component.
// This example shows how you might combine all methods into one functional mapDispatchToProps if needed:
const combinedMapDispatchToProps = (dispatch, ownProps) => ({
  ...mapDispatchToPropsA, // Spread the actions from the object shorthand
  ...mapDispatchToPropsB(dispatch), // Spread the actions from the functional dispatch
  ...mapDispatchToPropsC(dispatch, ownProps) // Spread the actions from the functional dispatch/ownProps
});

export default connect(mapStateToProps, combinedMapDispatchToProps)(CounterComponent);

