import React from 'react';
import axios from 'axios';
import { connect } from 'react-redux';

// Action Type
const SET_POSTS = 'SET_POSTS';

// Action Creator
const setPosts = (posts) => ({
  type: SET_POSTS,
  payload: posts
});

// Reducer (for completeness, though assumed to be set up in store)
/*
const initialPostState = { posts: [] };
function postReducer(state = initialPostState, action) {
  switch (action.type) {
    case SET_POSTS:
      return { ...state, posts: action.payload };
    default:
      return state;
  }
}
// In your actual store setup, this would be combined:
// const rootReducer = combineReducers({ postState: postReducer });
*/

class PostList extends React.Component {
  componentDidMount() {
    axios.get('/api/posts')
      .then(response => {
        // Dispatch an action to update Redux store instead of local state
        this.props.setPosts(response.data);
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
        // Optionally dispatch an error action here
      });
  }

  render() {
    // Render from props received from Redux store
    const { posts } = this.props;
    return (
      <div>
        <h2>Posts (Redux State)</h2>
        {posts.length === 0 ? (
          <p>Loading posts...</p>
        ) : (
          <ul>
            {posts.map(post => (
              <li key={post.id}>{post.title}</li>
            ))}
          </ul>
        )}
      </div>
    );
  }
}

// mapStateToProps: maps Redux state to component props
const mapStateToProps = (state) => ({
  posts: state.postState.posts // Assuming posts are under a 'postState' slice
});

// mapDispatchToProps: maps dispatch actions to component props
const mapDispatchToProps = (dispatch) => ({
  setPosts: (posts) => dispatch(setPosts(posts)) // Binds the setPosts action creator
});

// Connect the PostList component to the Redux store
export default connect(mapStateToProps, mapDispatchToProps)(PostList);

