import React from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import { thunk } from 'redux-thunk'; 

// --- Redux Setup (for demonstration, these would be in separate files) ---
// Action Types
const FETCH_POSTS_REQUEST = 'FETCH_POSTS_REQUEST';
const FETCH_POSTS_SUCCESS = 'FETCH_POSTS_SUCCESS';
const FETCH_POSTS_FAILURE = 'FETCH_POSTS_FAILURE';

// Action Creators
const fetchPostsRequest = () => ({ type: FETCH_POSTS_REQUEST });
const fetchPostsSuccess = (posts) => ({ type: FETCH_POSTS_SUCCESS, payload: posts });
const fetchPostsFailure = (error) => ({ type: FETCH_POSTS_FAILURE, payload: error });

// Thunk Action Creator
export const fetchPosts = () => {
  return async (dispatch) => {
    dispatch(fetchPostsRequest());
    try {
      const response = await axios.get('/api/posts');
      dispatch(fetchPostsSuccess(response.data));
    } catch (error) {
      dispatch(fetchPostsFailure(error.message));
    }
  };
};

// Reducer (to handle the different states of the fetch operation)
const initialPostState = { 
  posts: [], 
  loading: false, 
  error: null 
};

function postReducer(state = initialPostState, action) {
  switch (action.type) {
    case FETCH_POSTS_REQUEST:
      return { ...state, loading: true, error: null };
    case FETCH_POSTS_SUCCESS:
      return { ...state, loading: false, posts: action.payload, error: null };
    case FETCH_POSTS_FAILURE:
      return { ...state, loading: false, posts: [], error: action.payload };
    default:
      return state;
  }
}
// In a real application, you would combine this with other reducers
// For testing, assume `postReducer` is mounted at `state.postState`
// const rootReducer = combineReducers({ postState: postReducer });
// const store = createStore(rootReducer, applyMiddleware(thunk));
// ---------------------------------------------------------------------

class PostList extends React.Component {
  componentDidMount() {
    // Call the thunk action creator, which handles the async logic
    this.props.fetchPosts();
  }

  render() {
    const { posts, loading, error } = this.props;

    if (loading) return <p>Loading posts...</p>;
    if (error) return <p style={{ color: 'red' }}>Error: {error}</p>;
    if (posts.length === 0) return <p>No posts found.</p>;

    return (
      <div>
        <h2>Posts (Redux Thunk)</h2>
        <ul>
          {posts.map(post => (
            <li key={post.id}>{post.title}</li>
          ))}
        </ul>
      </div>
    );
  }
}

// mapStateToProps: maps state slices (posts, loading, error) to props
const mapStateToProps = (state) => ({
  posts: state.postState.posts,
  loading: state.postState.loading,
  error: state.postState.error
});

// mapDispatchToProps: maps the fetchPosts thunk action creator to props
// Using object shorthand works here because fetchPosts returns a function that Thunk handles
const mapDispatchToProps = {
  fetchPosts
};

export default connect(mapStateToProps, mapDispatchToProps)(PostList);

