import { Middleware, Dispatch, AnyAction, Store } from 'redux';

// Simplified RootState for demonstration
interface RootState { /* ... */ }

function createConditionalThunkMiddleware(extraArgument?: any): Middleware {
  return ({ dispatch, getState }) => next => action => {
    if (typeof action === 'function') {
      // Execute the thunk function.
      const thunkResult = action(dispatch, getState, extraArgument);

      // Only pass to next middleware if the thunk explicitly returns true
      if (thunkResult === true) {
        console.log('Thunk returned true, passing to next middleware.');
        // In this hypothetical scenario, 'next' would still expect a plain object.
        // So, you'd need to decide what plain action to pass.
        // For simplicity, we'll pass a placeholder or a resolved action.
        // In a real scenario, this design might be problematic as next expects an action.
        // For demonstration, let's assume if it returns true, we pass a generic 'THUNK_COMPLETED' action.
        return next({ type: 'THUNK_COMPLETED', payload: thunkResult });
      } else {
        console.log('Thunk did not return true, stopping chain.');
        return thunkResult; // Return whatever the thunk returned, stopping the chain for this action
      }
    } else {
      // If it's a normal action object, continue normal flow.
      return next(action);
    }
  };
}

// Example Thunk for this modified middleware:
const myConditionalThunk = (shouldContinue: boolean) => (
  dispatch: Dispatch,
  getState: () => RootState
) => {
  console.log('Executing conditional thunk...');
  dispatch({ type: 'THUNK_INTERNAL_ACTION' });
  return shouldContinue; // This return value determines if it proceeds
};

// Conceptual Usage:
// const store = createStore(rootReducer, applyMiddleware(createConditionalThunkMiddleware()));
// store.dispatch(myConditionalThunk(true));  // This would log THUNK_COMPLETED
// store.dispatch(myConditionalThunk(false)); // This would stop the chain after THUNK_INTERNAL_ACTION
```

