);

// --- Implement the thunk action creator `fetchUserById` ---
export const fetchUserById = (userId: string) => {
  return (dispatch: Dispatch, getState: GetState<RootState>) => {
    const state = getState();
    const userExists = state.users.data[userId];
    const userIsLoading = state.users.loadingUsers[userId];

    if (userExists) {
      console.log(`User ${userId} already in state. Not fetching.`);
      return;
    }
    if (userIsLoading) {
      console.log(`User ${userId} is already loading. Not re-fetching.`);
      return;
    }

